// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom'; // PORTAL İÇİN GEREKLİ
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, 
  FiFileText, FiX, FiEye, FiCheckSquare, FiSquare, FiChevronLeft, FiChevronRight, FiExternalLink, FiAirplay 
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  // İlk yükleme
  useEffect(() => {
    loadFiles();
  }, [currentProject]);

  // Listeyi çekme fonksiyonu (Tekrar kullanılmak üzere ayrıldı)
  const loadFiles = () => {
    if (currentProject) {
      setLoading(true);
      fetchFiles(currentProject.id)
        .then(data => setFiles(Array.isArray(data) ? data : []))
        .catch(() => setFiles([]))
        .finally(() => setLoading(false));
    }
  };

  // Klavye olayları
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!previewFile) return;
      if (e.key === 'ArrowRight') handleNextPrev('next');
      if (e.key === 'ArrowLeft') handleNextPrev('prev');
      if (e.key === 'Escape') setPreviewFile(null);
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  });

  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      // Dosyaları sırayla yükle
      for (const file of selectedFiles) {
        await uploadFile(currentProject.id, file);
      }
      // DÜZELTME: Yükleme bitince listeyi sunucudan taze çek.
      // Bu sayede "listede gözükmüyor" sorunu kesin çözülür.
      loadFiles(); 
    } catch (error) {
      console.error("Upload failed", error);
      alert("Error: Some files couldn't be uploaded.");
    } finally {
      setUploading(false);
      e.target.value = null; 
    }
  };

  const handleDeleteSingle = async (id) => {
    if (window.confirm("Delete this file?")) {
      try {
        await deleteFile(currentProject.id, id);
        setFiles(prev => prev.filter(f => f.id !== id));
        setPreviewFile(null);
      } catch (err) {
        alert("Delete failed.");
      }
    }
  };

  const handleDeleteSelected = async () => {
    if (window.confirm(`Delete ${selectedIds.length} files?`)) {
      setLoading(true);
      try {
        await Promise.all(selectedIds.map(id => deleteFile(currentProject.id, id)));
        // Hızlı güncelleme için state'ten siliyoruz
        setFiles(prev => prev.filter(f => !selectedIds.includes(f.id)));
        setSelectedIds([]);
      } catch (err) {
        alert("Error during batch delete.");
        loadFiles(); // Hata varsa listeyi yenile
      } finally {
        setLoading(false);
      }
    }
  };

  const toggleSelect = (id, e) => {
    e.stopPropagation();
    if (selectedIds.includes(id)) {
      setSelectedIds(selectedIds.filter(itemId => itemId !== id));
    } else {
      setSelectedIds([...selectedIds, id]);
    }
  };

  const toggleSelectAll = () => {
    if (selectedIds.length === files.length) {
      setSelectedIds([]);
    } else {
      setSelectedIds(files.map(f => f.id));
    }
  };

  const handleNextPrev = (direction) => {
    if (!previewFile) return;
    const currentIndex = files.findIndex(f => f.id === previewFile.id);
    let newIndex;
    if (direction === 'next') {
      newIndex = currentIndex + 1 < files.length ? currentIndex + 1 : 0; 
    } else {
      newIndex = currentIndex - 1 >= 0 ? currentIndex - 1 : files.length - 1; 
    }
    setPreviewFile(files[newIndex]);
  };

  const getFileUrl = (filePath) => {
    if (!filePath) return '';
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  const getFileType = (filename) => {
    if (!filename) return 'other';
    const ext = filename.split('.').pop().toLowerCase();
    
    if (['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg'].includes(ext)) return 'image';
    if (ext === 'pdf') return 'pdf';
    if (['doc', 'docx'].includes(ext)) return 'word';
    if (['ppt', 'pptx'].includes(ext)) return 'powerpoint';
    if (['txt', 'md', 'json', 'log', 'js', 'css', 'html'].includes(ext)) return 'text';
    
    return 'other';
  };

  const getPreviewUrl = (file) => {
    const url = getFileUrl(file.file_path);
    const type = getFileType(file.filename);
    
    // Resim, PDF ve Text doğrudan iframe içinde açılabilir
    if (type === 'image' || type === 'pdf' || type === 'text') {
      return url;
    }
    
    // Word ve PPT için Microsoft Viewer
    // NOT: Localhost'ta çalışıyorsanız bu viewer dosyanızı göremez.
    // Dosyanızın internete açık bir URL'de olması gerekir.
    return `https://view.officeapps.live.com/op/embed.aspx?src=${encodeURIComponent(url)}`;
  };

  // --- RENDER ---
  return (
    <div className="w-full space-y-8">
      {/* HEADER */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
        <div className="flex items-center gap-4">
          <h2 className="text-2xl font-black text-text-main tracking-tight">Project Overview</h2>
          {selectedIds.length > 0 && canEdit && (
            <button onClick={handleDeleteSelected} className="flex items-center px-4 py-2 bg-red-600 text-white rounded-xl text-xs font-bold border-none cursor-pointer hover:bg-red-700 transition-colors">
              <FiTrash2 className="mr-2" /> Delete Selected ({selectedIds.length})
            </button>
          )}
        </div>
        
        {canEdit && (
          <div className="relative">
            <input 
              type="file" multiple onChange={handleFileUpload} 
              accept=".jpg,.jpeg,.png,.pdf,.doc,.docx,.ppt,.pptx,.txt,.md" 
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10" 
              disabled={uploading}
            />
            <button className={`flex items-center px-6 py-2.5 rounded-xl font-bold transition-all ${uploading ? 'bg-text-secondary' : 'bg-primary hover:bg-primary-hover'} text-white shadow-lg`}>
              {uploading ? <FiLoader className="animate-spin mr-2" /> : <FiUpload className="mr-2" />}
              {uploading ? 'Uploading...' : 'Upload Files'}
            </button>
          </div>
        )}
      </div>

      {/* README PANEL */}
      <div className="bg-surface rounded-2xl border border-border shadow-sm overflow-hidden flex flex-col">
        <div className="bg-app/50 px-5 py-3 border-b border-border flex items-center gap-2">
           <FiFileText className="text-primary" />
           <span className="text-xs font-black uppercase tracking-widest text-text-secondary">Documentation / README</span>
        </div>
        <div className="p-6 max-h-[300px] overflow-y-auto custom-scrollbar bg-surface/50 font-medium text-text-main leading-relaxed whitespace-pre-wrap">
          {currentProject?.long_description || "No detailed documentation provided."}
        </div>
      </div>

      {/* FILES TABLE */}
      <div className="bg-surface rounded-2xl overflow-hidden border border-border shadow-sm">
        <div className="flex items-center bg-app/80 p-4 border-b border-border text-[10px] font-black text-text-secondary uppercase tracking-widest">
          <div className="w-10 text-center">
            {canEdit && (
              <button onClick={toggleSelectAll} className="hover:text-text-main transition-colors border-none bg-transparent cursor-pointer">
                {files.length > 0 && selectedIds.length === files.length ? <FiCheckSquare size={16} className="text-primary"/> : <FiSquare size={16}/>}
              </button>
            )}
          </div>
          <div className="flex-1 px-4 text-left">Filename</div>
          <div className="w-32 hidden sm:block text-left">Uploaded By</div>
          <div className="w-32 text-right hidden sm:block">Date</div>
          <div className="w-16 text-center">View</div>
        </div>

        <ul className="divide-y divide-border m-0 p-0 list-none">
          {files.map((file, idx) => {
            if (!file || !file.filename) return null;
            const type = getFileType(file.filename);
            const isSelected = selectedIds.includes(file.id);

            return (
              <li key={file.id || idx} onClick={() => setPreviewFile(file)} className={`flex items-center p-4 hover:bg-app/50 transition-all cursor-pointer group ${isSelected ? 'bg-primary/5' : ''}`}>
                <div className="w-10 flex justify-center" onClick={(e) => e.stopPropagation()}>
                  {canEdit && (
                    <button onClick={(e) => toggleSelect(file.id, e)} className={`transition-colors border-none bg-transparent cursor-pointer ${isSelected ? 'text-primary' : 'text-text-secondary hover:text-text-main'}`}>
                      {isSelected ? <FiCheckSquare size={18}/> : <FiSquare size={18}/>}
                    </button>
                  )}
                </div>
                <div className="flex-1 px-4 flex items-center min-w-0">
                  <div className={`p-2.5 rounded-xl mr-4 shadow-sm ${
                    type === 'word' ? 'bg-blue-500/10 text-blue-600' : 
                    type === 'powerpoint' ? 'bg-orange-500/10 text-orange-600' : 
                    type === 'text' ? 'bg-emerald-500/10 text-emerald-600' : 
                    type === 'image' ? 'bg-purple-500/10 text-purple-600' : 'bg-app text-text-secondary'
                  }`}>
                    {type === 'word' ? <FiFileText size={18} /> : type === 'powerpoint' ? <FiAirplay size={18} /> : <FiFile size={18} />}
                  </div>
                  <span className="text-text-main font-bold truncate text-sm">{file.filename}</span>
                </div>
                <div className="w-32 text-xs font-bold text-text-secondary hidden sm:block truncate uppercase tracking-tighter">{file.uploader_name}</div>
                <div className="w-32 text-right text-xs font-bold text-text-secondary hidden sm:block font-mono opacity-60">{new Date(file.created_at).toLocaleDateString()}</div>
                <div className="w-16 flex justify-center"><FiEye className="text-text-secondary group-hover:text-primary transition-colors" size={18} /></div>
              </li>
            );
          })}
        </ul>
        {files.length === 0 && !loading && (
           <div className="p-8 text-center text-text-secondary italic">No files uploaded yet.</div>
        )}
      </div>

      {/* --- PORTAL İLE TAM EKRAN PREVIEW --- */}
      {previewFile && ReactDOM.createPortal(
        <div className="fixed inset-0 w-screen h-screen z-[99999] bg-black flex flex-col overflow-hidden animate-fade-in" style={{ margin: 0, padding: 0 }}>
          
          {/* Top Control Bar */}
          <div className="flex-none w-full flex items-center justify-between px-6 py-4 bg-black/80 backdrop-blur-md border-b border-white/10 z-[100]">
              <div className="flex items-center gap-4 text-white">
                  <button onClick={() => setPreviewFile(null)} className="p-2 hover:bg-white/10 rounded-full transition-all border-none bg-transparent cursor-pointer text-white">
                      <FiX size={28} />
                  </button>
                  <span className="font-bold truncate max-w-xs md:max-w-md">{previewFile.filename}</span>
              </div>
              
              <div className="flex items-center gap-3">
                  <a href={getFileUrl(previewFile.file_path)} target="_blank" rel="noopener noreferrer" className="px-5 py-2.5 bg-white/10 hover:bg-white/20 text-white rounded-xl text-xs font-bold transition-all flex items-center gap-2 no-underline border-none cursor-pointer">
                      <FiExternalLink size={18} /> View Large
                  </a>
                  <a href={getFileUrl(previewFile.file_path)} download className="px-5 py-2.5 bg-primary hover:bg-primary-hover text-white rounded-xl text-xs font-black transition-all flex items-center gap-2 no-underline border-none shadow-xl cursor-pointer">
                      <FiDownload size={18} /> Download
                  </a>
                  {canEdit && (
                      <button onClick={() => handleDeleteSingle(previewFile.id)} className="p-2.5 bg-red-600/20 hover:bg-red-600 text-red-500 hover:text-white rounded-xl transition-all border-none cursor-pointer shadow-lg">
                          <FiTrash2 size={20} />
                      </button>
                  )}
              </div>
          </div>

          {/* Main Content Area */}
          <div className="flex-1 relative w-full h-full bg-[#0a0a0a] flex items-center justify-center p-0 overflow-hidden">
            
            {/* Nav Arrows */}
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('prev'); }} className="absolute left-6 z-[120] text-white/30 hover:text-white p-4 hover:bg-white/10 rounded-full transition-all border-none cursor-pointer">
                <FiChevronLeft size={56} />
            </button>
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('next'); }} className="absolute right-6 z-[120] text-white/30 hover:text-white p-4 hover:bg-white/10 rounded-full transition-all border-none cursor-pointer">
                <FiChevronRight size={56} />
            </button>

            {/* Content Injection */}
            <div className="w-full h-full flex items-center justify-center">
                {getFileType(previewFile.filename) === 'image' ? (
                  <img src={getFileUrl(previewFile.file_path)} alt="Preview" className="max-w-full max-h-full object-contain animate-scale-in drop-shadow-2xl"/>
                ) : (
                  // Microsoft Viewer for Office, native iframe for others
                  <iframe 
                    src={getPreviewUrl(previewFile)} 
                    title="File Preview" 
                    className="w-full h-full border-none bg-white" 
                  />
                )}
            </div>
            
            {/* Pagination Info */}
            <div className="absolute bottom-8 left-1/2 -translate-x-1/2 z-[120]">
                <span className="bg-black/60 text-white/60 px-6 py-2 rounded-full text-[10px] font-black tracking-widest uppercase backdrop-blur-md border border-white/5">
                    {files.findIndex(f => f.id === previewFile.id) + 1} / {files.length}
                </span>
            </div>
          </div>
        </div>,
        document.body // PORTAL HEDEFİ: BODY
      )}
    </div>
  );
}
