// src/components/projects/ProjectEditPage.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { FiInfo, FiShare, FiLoader, FiCheck, FiAlertTriangle, FiGlobe, FiLock } from 'react-icons/fi';

// Bu component, 'Edit' sekmesinin içeriğidir
export default function ProjectEditPage() {
  
  // 1. Context'ten gerekli verileri ve fonksiyonları al
  const { 
    currentProject, 
    addMember, 
    updateProjectVisibility 
  } = useProjectContext();

  // 2. "Share" (Üye Ekle) formu için state'ler
  const [usernameToShare, setUsernameToShare] = useState('');
  const [isSharing, setIsSharing] = useState(false);
  const [shareError, setShareError] = useState(null);
  const [shareSuccess, setShareSuccess] = useState(null);

  // 3. "Görünürlük" (Public/Private) state'i
  const [isPublic, setIsPublic] = useState(currentProject?.is_public || false);
  const [updatingVisibility, setUpdatingVisibility] = useState(false);

  // 4. 'currentProject' prop'u değiştiğinde (örn: 'Make Public'e basıldığında)
  //    'isPublic' state'ini senkronize et
  useEffect(() => {
    if (currentProject) {
      setIsPublic(currentProject.is_public);
    }
  }, [currentProject]);


  // Proje verisi henüz yüklenmediyse bekle
  if (!currentProject) {
    return null;
  }

  // 5. "Share" (Üye Ekle) butonuna basıldığında
  const handleShareSubmit = async (e) => {
    e.preventDefault();
    if (!usernameToShare) {
      setShareError('Please enter a username.');
      return;
    }

    setIsSharing(true);
    setShareError(null);
    setShareSuccess(null);

    try {
      // Context'teki 'addMember' fonksiyonunu çağır
      const addedMember = await addMember(currentProject.id, usernameToShare);
      
      // Başarılı
      setShareSuccess(`Successfully shared with ${addedMember.username}!`);
      setUsernameToShare(''); // Input'u temizle
    } catch (err) {
      // Başarısız (örn: "User not found." veya "User is already a member.")
      setShareError(err.message);
    } finally {
      setIsSharing(false);
    }
  };

  // 6. "Make Public" / "Make Private" butonuna basıldığında
  const handleVisibilityToggle = async () => {
    setUpdatingVisibility(true);
    try {
      const newVisibility = !isPublic; // Durumu tersine çevir
      // Context'teki 'updateProjectVisibility' fonksiyonunu çağır
      await updateProjectVisibility(currentProject.id, newVisibility);
      setIsPublic(newVisibility); // Yerel state'i güncelle
    } catch (error) {
      console.error("Failed to update visibility", error);
      // (Burada bir hata mesajı gösterebilirsiniz)
    } finally {
      setUpdatingVisibility(false);
    }
  };


  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4 text-white">
        Edit Project Settings
      </h2>
      
      {/* === 1. BÖLÜM: PROJE GÖRÜNÜRLÜĞÜ === */}
      <div className="bg-gray-800 p-6 rounded-lg mb-6">
        <h3 className="text-xl font-semibold text-white mb-4">
          Project Visibility
        </h3>
        {isPublic ? (
          <p className="text-gray-400 text-sm mb-4">
            This project is currently <span className="font-bold text-green-400">Public</span>.
            Any logged-in user can view and comment.
          </p>
        ) : (
          <p className="text-gray-400 text-sm mb-4">
            This project is currently <span className="font-bold text-red-400">Private</span>.
            Only invited members can access it.
          </p>
        )}
        <button
          onClick={handleVisibilityToggle}
          disabled={updatingVisibility}
          className={`flex items-center justify-center px-4 py-2 font-semibold rounded-lg transition-colors disabled:opacity-50 ${
            isPublic
              ? 'bg-red-600 hover:bg-red-700 text-white'
              : 'bg-green-600 hover:bg-green-700 text-white'
          }`}
        >
          {updatingVisibility ? (
            <FiLoader className="animate-spin" />
          ) : isPublic ? (
            <><FiLock className="mr-2" /> Make Private</>
          ) : (
            <><FiGlobe className="mr-2" /> Make Public</>
          )}
        </button>
      </div>

      {/* === 2. BÖLÜM: "SHARE" (ÜYE EKLE) FORMU === */}
      <div className="bg-gray-800 p-6 rounded-lg mb-6">
        <h3 className="text-xl font-semibold text-white mb-4">
          Share This Project (Invite Members)
        </h3>
        <p className="text-gray-400 text-sm mb-4">
          Enter the username of the member you want to share this project with.
          They will be added as a 'Viewer' and can read and comment.
        </p>
        
        <form onSubmit={handleShareSubmit}>
          <label htmlFor="shareUsername" className="block text-sm font-medium text-gray-300 mb-1">
            Username
          </label>
          <div className="flex space-x-2">
            <input
              type="text"
              id="shareUsername"
              value={usernameToShare}
              onChange={(e) => {
                setUsernameToShare(e.target.value);
                setShareError(null); // Yazmaya başlayınca hataları temizle
                setShareSuccess(null);
              }}
              className="flex-grow py-2 px-3 text-gray-200 bg-gray-700 border border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="username_to_share"
            />
            <button
              type="submit"
              disabled={isSharing}
              className="flex items-center justify-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg transition-colors disabled:opacity-50"
            >
              {isSharing ? (
                <FiLoader className="animate-spin" />
              ) : (
                <FiShare className="mr-2" />
              )}
              Share
            </button>
          </div>
          {/* Başarı veya Hata Mesajları */}
          {shareSuccess && (
            <p className="text-sm text-green-400 mt-2 flex items-center">
              <FiCheck className="mr-1" /> {shareSuccess}
            </p>
          )}
          {shareError && (
            <p className="text-sm text-red-400 mt-2 flex items-center">
              <FiAlertTriangle size={14} className="mr-1" />
              {shareError}
            </p>
          )}
        </form>
      </div>

      {/* === 3. BÖLÜM: TEHLİKE ALANI (PROJE SİLME) === */}
      <div className="bg-gray-800 p-6 rounded-lg">
        <h3 className="text-xl font-semibold text-red-500 mb-4">
          Danger Zone
        </h3>
        <p className="text-gray-400 text-sm mb-4">
          Deleting this project is a permanent action and cannot be undone.
        </p>
        <button
          onClick={() => alert('Proje silme fonksiyonu eklenecek!')}
          className="bg-red-800 hover:bg-red-700 text-white font-bold py-2 px-4 rounded-lg shadow-lg"
        >
          Delete This Project
        </button>
      </div>
    </div>
  );
}
