// src/pages/MyProjectsPage.js

import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useProjectContext } from '../context/ProjectContext';
import { FiFolder, FiPlus, FiClock } from 'react-icons/fi';

export default function MyProjectsPage() {
  // DÜZELTME: 'projects' yerine 'myProjects' ve 'fetchMyProjects' kullanıyoruz
  const { myProjects, fetchMyProjects, loading } = useProjectContext();

  useEffect(() => {
    // Sadece benim projelerimi getir
    fetchMyProjects();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-gray-400 animate-pulse">Loading projects...</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-6">
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-2xl font-bold text-white">My Projects</h1>
          <p className="text-gray-400 text-sm mt-1">
            Projects you created ({myProjects.length})
          </p>
        </div>
        
        {/* Opsiyonel: Yeni proje ekleme butonu */}
        {/* Bu buton genelde Dashboard'da olur ama buraya da koyabiliriz */}
      </div>

      {myProjects.length === 0 ? (
        <div className="text-center py-12 bg-gray-800 rounded-xl border border-gray-700 border-dashed">
          <FiFolder className="mx-auto h-12 w-12 text-gray-600 mb-4" />
          <h3 className="text-lg font-medium text-gray-300">No projects yet</h3>
          <p className="text-gray-500 mt-2">Create your first project from the dashboard.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {myProjects.map((project) => (
            <Link 
              key={project.id} 
              to={`/project/${project.id}`}
              className="group block bg-gray-800 rounded-xl border border-gray-700 p-6 hover:border-blue-500 transition-all duration-200 hover:shadow-lg hover:shadow-blue-900/10"
            >
              <div className="flex justify-between items-start mb-4">
                <div className="p-3 bg-blue-900/30 rounded-lg text-blue-400 group-hover:bg-blue-600 group-hover:text-white transition-colors">
                  <FiFolder size={24} />
                </div>
                <span className={`text-xs px-2 py-1 rounded-full border ${
                  project.is_public 
                    ? 'bg-green-900/30 text-green-400 border-green-800' 
                    : 'bg-gray-700 text-gray-400 border-gray-600'
                }`}>
                  {project.is_public ? 'Public' : 'Private'}
                </span>
              </div>

              <h3 className="text-xl font-bold text-white mb-2 group-hover:text-blue-400 transition-colors">
                {project.name}
              </h3>
              
              <p className="text-gray-400 text-sm line-clamp-2 mb-6 h-10">
                {project.description || "No description provided."}
              </p>

              <div className="flex items-center text-xs text-gray-500 pt-4 border-t border-gray-700">
                <FiClock className="mr-1.5" />
                <span>Updated: {new Date(project.last_updated_at || project.created_at).toLocaleDateString()}</span>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
}
