// src/components/projects/ProjectTasks.js

import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiPlus, FiTrash2, FiClock, FiCheckCircle, FiAlertCircle 
} from 'react-icons/fi';

const priorityColors = {
  low: 'bg-blue-100 text-blue-700 dark:bg-blue-900/30 dark:text-blue-400 border border-blue-200 dark:border-blue-800',
  medium: 'bg-yellow-100 text-yellow-700 dark:bg-yellow-900/30 dark:text-yellow-400 border border-yellow-200 dark:border-yellow-800',
  high: 'bg-red-100 text-red-700 dark:bg-red-900/30 dark:text-red-400 border border-red-200 dark:border-red-800',
};

export default function ProjectTasks() {
  const { currentProject, fetchTasks, tasks, createTask, updateTaskStatus, deleteTask } = useProjectContext();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newTaskTitle, setNewTaskTitle] = useState('');
  const [newTaskPriority, setNewTaskPriority] = useState('medium');
  const [newTaskStatus, setNewTaskStatus] = useState('todo');

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  useEffect(() => {
    if (currentProject?.id) fetchTasks(currentProject.id);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentProject]);

  const onDragEnd = async (result) => {
    if (!canEdit) return;
    if (!result.destination) return;
    const { draggableId, destination } = result;
    if (destination.droppableId !== result.source.droppableId) {
       await updateTaskStatus(currentProject.id, draggableId, destination.droppableId);
    }
  };

  const handleCreateTask = async (e) => {
    e.preventDefault();
    if (!newTaskTitle.trim()) return;
    await createTask(currentProject.id, newTaskTitle, newTaskStatus, newTaskPriority);
    setNewTaskTitle('');
    setIsModalOpen(false);
  };

  const openNewTaskModal = (status) => {
    setNewTaskStatus(status);
    setNewTaskTitle('');
    setIsModalOpen(true);
  };

  const columns = {
    todo: { title: 'To Do', items: tasks.filter(t => t.status === 'todo') },
    in_progress: { title: 'In Progress', items: tasks.filter(t => t.status === 'in_progress') },
    done: { title: 'Done', items: tasks.filter(t => t.status === 'done') }
  };

  return (
    <div className="h-[calc(100vh-220px)] flex flex-col">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-text-main flex items-center">
            <FiCheckCircle className="mr-3 text-primary" /> Kanban Board
        </h2>
      </div>

      <div className="flex-1 overflow-x-auto overflow-y-hidden pb-4">
        <DragDropContext onDragEnd={onDragEnd}>
          <div className="flex h-full gap-6 min-w-[800px]">
            {Object.entries(columns).map(([columnId, column]) => (
              <div key={columnId} className="flex-1 flex flex-col min-w-[280px] max-w-sm">
                
                {/* KOLON BAŞLIĞI: bg-surface ve border-border kullanıldı */}
                <div className={`flex justify-between items-center p-4 bg-surface border border-border rounded-t-2xl border-b-2 ${columnId === 'todo' ? 'border-b-red-500' : columnId === 'in_progress' ? 'border-b-yellow-500' : 'border-b-green-500'} shadow-sm`}>
                  <div className="flex items-center gap-2">
                    <span className="font-bold text-text-main uppercase tracking-wider text-sm">{column.title}</span>
                    <span className="bg-app text-text-secondary px-2 py-0.5 rounded text-xs font-bold border border-border">
                        {column.items.length}
                    </span>
                  </div>
                  {canEdit && (
                    <button onClick={() => openNewTaskModal(columnId)} className="text-text-secondary hover:text-primary transition-colors p-1 rounded hover:bg-app">
                        <FiPlus size={18} />
                    </button>
                  )}
                </div>

                {/* KOLON GÖVDESİ: bg-app kullanıldı */}
                <Droppable droppableId={columnId}>
                  {(provided, snapshot) => (
                    <div
                      {...provided.droppableProps}
                      ref={provided.innerRef}
                      className={`flex-1 p-3 bg-app/50 border-x border-b border-border rounded-b-2xl overflow-y-auto custom-scrollbar transition-colors ${snapshot.isDraggingOver ? 'bg-primary/5 ring-2 ring-inset ring-primary/20' : ''}`}
                    >
                      {column.items.map((task, index) => (
                        <Draggable key={task.id} draggableId={task.id} index={index} isDragDisabled={!canEdit}>
                          {(provided, snapshot) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              // KART: bg-surface, text-text-main
                              className={`p-4 mb-3 bg-surface rounded-xl border border-border shadow-sm group transition-all hover:shadow-md ${snapshot.isDragging ? 'shadow-2xl ring-2 ring-primary rotate-2 opacity-90' : ''}`}
                            >
                              <div className="flex justify-between items-start mb-2">
                                <span className={`text-[10px] uppercase font-bold px-2 py-0.5 rounded ${priorityColors[task.priority]}`}>
                                    {task.priority}
                                </span>
                                {canEdit && (
                                  <button onClick={() => deleteTask(currentProject.id, task.id)} className="text-text-secondary hover:text-red-500 opacity-0 group-hover:opacity-100 transition-opacity p-1">
                                    <FiTrash2 size={14} />
                                  </button>
                                )}
                              </div>
                              <h4 className="font-bold text-text-main text-sm mb-3 leading-snug">{task.title}</h4>
                              <div className="flex items-center text-[10px] text-text-secondary font-medium">
                                <FiClock className="mr-1" />
                                {new Date(task.created_at).toLocaleDateString()}
                              </div>
                            </div>
                          )}
                        </Draggable>
                      ))}
                      {provided.placeholder}
                      {column.items.length === 0 && !snapshot.isDraggingOver && (
                          <div className="h-full flex flex-col items-center justify-center text-text-secondary opacity-40">
                              <FiAlertCircle size={24} className="mb-2"/>
                              <span className="text-xs font-medium">No tasks</span>
                          </div>
                      )}
                    </div>
                  )}
                </Droppable>
              </div>
            ))}
          </div>
        </DragDropContext>
      </div>

      {/* MODAL: bg-surface ve inputlarda bg-app */}
      {isModalOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm animate-fade-in">
          <div className="bg-surface border border-border rounded-2xl p-6 w-full max-w-sm shadow-2xl">
            <h3 className="text-lg font-bold text-text-main mb-4">Add Task to "{columns[newTaskStatus].title}"</h3>
            <form onSubmit={handleCreateTask} className="space-y-4">
              <div>
                  <label className="block text-xs font-bold text-text-secondary uppercase mb-1">Title</label>
                  <input
                    autoFocus
                    type="text"
                    className="w-full bg-app border border-border rounded-xl px-4 py-2.5 text-text-main outline-none focus:border-primary transition-colors"
                    value={newTaskTitle}
                    onChange={(e) => setNewTaskTitle(e.target.value)}
                    placeholder="What needs to be done?"
                  />
              </div>
              <div>
                 <label className="block text-xs font-bold text-text-secondary uppercase mb-1">Priority</label>
                 <select
                    value={newTaskPriority}
                    onChange={(e) => setNewTaskPriority(e.target.value)}
                    className="w-full bg-app border border-border rounded-xl px-4 py-2.5 text-text-main outline-none focus:border-primary cursor-pointer"
                  >
                    <option value="low">Low</option>
                    <option value="medium">Medium</option>
                    <option value="high">High</option>
                  </select>
              </div>
              <div className="flex justify-end gap-2 pt-2">
                <button type="button" onClick={() => setIsModalOpen(false)} className="px-4 py-2 bg-app hover:bg-surface-hover border border-border rounded-xl text-text-main font-bold text-sm transition-colors">Cancel</button>
                <button type="submit" className="px-4 py-2 bg-primary hover:bg-primary-hover text-white rounded-xl font-bold text-sm shadow-md transition-colors">Add Task</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
