// src/pages/SharedProjectsPage.js
import React, { useState, useEffect, useMemo } from 'react';
import { useProjectContext } from '../context/ProjectContext';
import { FiLoader, FiSearch, FiGlobe, FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import SharedProjectCard from '../components/projects/SharedProjectCard';

function SharedProjectsPage() {
  const { sharedProjects, loading, fetchSharedProjects } = useProjectContext();
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const projectsPerPage = 6;

  useEffect(() => {
    fetchSharedProjects(); // Backend listPublicProjects döndürmeli
  }, []);

  const publicProjects = useMemo(() => {
    // Sadece public projeleri ve arama sonucunu göster 
    return sharedProjects.filter(p => p.is_public && p.name.toLowerCase().includes(searchTerm.toLowerCase()));
  }, [sharedProjects, searchTerm]);

  const currentProjects = publicProjects.slice((currentPage - 1) * projectsPerPage, currentPage * projectsPerPage);
  const totalPages = Math.max(Math.ceil(publicProjects.length / projectsPerPage), 1);

  return (
    <div className="flex flex-col min-h-screen p-6 bg-app">
      <h1 className="text-3xl font-black text-text-main mb-8">Public Explorer</h1>
      
      <div className="relative mb-8 max-w-md bg-surface p-2 rounded-2xl border border-border shadow-sm">
        <FiSearch className="absolute left-5 top-5 text-text-secondary" />
        <input type="text" placeholder="Search public projects..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)}
               className="w-full bg-app border border-border text-text-main rounded-xl pl-10 py-2 focus:border-primary outline-none" />
      </div>

      <div className="flex-grow">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {currentProjects.map(project => (
            <SharedProjectCard key={project.id} project={project} viewMode="grid" />
          ))}
        </div>
      </div>

      {/* Sabit Sayfalama */}
      <div className="mt-auto pt-10 pb-4">
        <div className="flex justify-between items-center bg-surface border border-border p-4 rounded-2xl shadow-md">
          <span className="text-sm text-text-secondary">Showing {publicProjects.length} public projects</span>
          <div className="flex gap-2">
            <button onClick={() => setCurrentPage(p => Math.max(1, p - 1))} disabled={currentPage === 1} className="p-2 border border-border rounded-xl disabled:opacity-20"><FiChevronLeft /></button>
            <button onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))} disabled={currentPage === totalPages} className="p-2 border border-border rounded-xl disabled:opacity-20"><FiChevronRight /></button>
          </div>
        </div>
      </div>
    </div>
  );
}
export default SharedProjectsPage;
