// src/pages/MyProjectsPage.js
import React, { useState, useEffect, useMemo } from 'react';
import CreateProjectModal from '../components/projects/CreateProjectModal';
import { Link } from 'react-router-dom';
import { useProjectContext } from '../context/ProjectContext';
import { useAuth } from '../context/AuthContext';
import { 
  FiLoader, FiPlus, FiGrid, FiList, FiSearch, FiGlobe, FiLock, FiChevronLeft, FiChevronRight, FiUser, FiClock 
} from 'react-icons/fi';

function MyProjectsPage() {
  const { user } = useAuth();
  const { myProjects, sharedProjects, loading, fetchMyProjects, fetchSharedProjects } = useProjectContext();
  
  const [isModalOpen, setIsModalOpen] = useState(false); 
  const [viewMode, setViewMode] = useState('grid'); 
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  
  // ƒ∞STEDƒ∞ƒûƒ∞Nƒ∞Z 2 Fƒ∞LTRE
  const [ownershipFilter, setOwnershipFilter] = useState('all'); // all, owner, member
  const [visibilityFilter, setVisibilityFilter] = useState('all'); // all, public, private

  const projectsPerPage = viewMode === 'grid' ? 6 : 5;

  useEffect(() => {
    fetchMyProjects();
    fetchSharedProjects();
  }, []);

  // --- √úYELƒ∞K KONTROL√ú VE VERƒ∞ Bƒ∞RLE≈ûTƒ∞RME ---
 const joinedProjects = useMemo(() => {
  if (!user || !user.id) return [];

  // 1. Sahibi olduƒüum projeler (Tip uyu≈ümazlƒ±ƒüƒ±nƒ± √∂nlemek i√ßin Number kullanƒ±yoruz)
  const owned = myProjects.map(p => ({ 
    ...p, 
    _role: 'owner' 
  }));

  // 2. √úyesi olduƒüum projeler
  const shared = sharedProjects.map(p => ({ 
    ...p, 
    _role: 'member' 
  }));

  // Filtreleme yapmadan √∂nce iki listeyi birle≈ütiriyoruz
  const all = [...owned, ...shared];

  // M√ºkerrer kayƒ±tlarƒ± (hem owner hem member gibi g√∂r√ºnenleri) ID √ºzerinden temizle
  const unique = [];
  const map = new Map();
  for (const item of all) {
    if(!map.has(item.id)){
        map.set(item.id, true);
        unique.push(item);
    }
  }
  return unique;
}, [myProjects, sharedProjects, user]);

  // --- Fƒ∞LTRELEME MANTIƒûI ---
  const filteredProjects = useMemo(() => {
    return joinedProjects.filter(project => {
      // Arama Filtresi
      const matchesSearch = project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()));
      
      // Filtre 1: Sahiplik Durumu (Benim kurduklarƒ±m / Katƒ±ldƒ±klarƒ±m)
      const matchesOwnership = ownershipFilter === 'all' ? true : project._role === ownershipFilter;
      
      // Filtre 2: G√∂r√ºn√ºrl√ºk Durumu (Public / Private)
      const matchesVisibility = visibilityFilter === 'all' ? true : 
                               visibilityFilter === 'public' ? project.is_public === true : 
                               project.is_public === false;

      return matchesSearch && matchesOwnership && matchesVisibility;
    });
  }, [joinedProjects, searchTerm, ownershipFilter, visibilityFilter]);

  // Sayfalama
  const currentProjects = filteredProjects.slice((currentPage - 1) * projectsPerPage, currentPage * projectsPerPage);
  const totalPages = Math.max(Math.ceil(filteredProjects.length / projectsPerPage), 1);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  useEffect(() => { setCurrentPage(1); }, [searchTerm, ownershipFilter, visibilityFilter]);

  return (
    // min-h-screen ve overflow-hidden ile dƒ±≈ü √ßer√ßeveyi sabitliyoruz
    <div className="flex flex-col h-[calc(100vh-140px)] p-6 bg-app transition-colors duration-300 overflow-hidden">
      <div className="max-w-7xl mx-auto w-full h-full flex flex-col">
        
        {/* √úST PANEL: BA≈ûLIK VE Fƒ∞LTRELER (SABƒ∞T) */}
        <div className="flex-none space-y-6 mb-8">
          <div className="flex justify-between items-center">
            <h1 className="text-3xl font-black text-text-main tracking-tight dark:text-white">My Workspace</h1>
            <button 
              onClick={() => setIsModalOpen(true)} 
              className="bg-primary hover:opacity-90 text-white font-bold py-2.5 px-6 rounded-xl shadow-lg transition-all transform hover:scale-105 active:scale-95 flex items-center border-none"
            >
              <FiPlus className="mr-2" /> New Project
            </button>
          </div>

          {/* Fƒ∞LTRE PANELƒ∞ */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 bg-surface p-4 rounded-2xl shadow-sm dark:bg-surface/10">
            <div className="relative col-span-1 md:col-span-2 group">
              <FiSearch className="absolute left-4 top-3.5 text-text-secondary opacity-40 group-focus-within:text-primary transition-colors" />
              <input
                type="text"
                placeholder="Search projects..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full bg-app border-none text-text-main text-sm rounded-xl pl-12 py-3 outline-none focus:ring-2 focus:ring-primary/20 transition-all shadow-inner"
              />
            </div>
            
            <select 
              value={ownershipFilter} 
              onChange={(e) => setOwnershipFilter(e.target.value)} 
              className="w-full bg-app border-none text-text-main text-sm rounded-xl px-4 py-3 outline-none focus:ring-2 focus:ring-primary/20 cursor-pointer appearance-none shadow-inner"
            >
              <option value="all">üë• All Roles</option>
              <option value="owner">üëë Owner</option>
              <option value="member">üõ†Ô∏è Member</option>
            </select>

            <select 
              value={visibilityFilter} 
              onChange={(e) => setVisibilityFilter(e.target.value)} 
              className="w-full bg-app border-none text-text-main text-sm rounded-xl px-4 py-3 outline-none focus:ring-2 focus:ring-primary/20 cursor-pointer appearance-none shadow-inner"
             Chad>
              <option value="all">üåê All Visibility</option>
              <option value="public">üåç Public</option>
              <option value="private">üîí Private</option>
            </select>
          </div>
        </div>

        {/* PROJE Lƒ∞STESƒ∞ ALANI (KAYDIRILABƒ∞Lƒ∞R) */}
        <div className="flex-grow overflow-y-auto pr-2 custom-scrollbar">
          {loading ? (
            <div className="flex justify-center p-20"><FiLoader className="animate-spin text-primary" size={50} /></div>
          ) : filteredProjects.length === 0 ? (
            <div className="bg-surface/30 p-20 rounded-2xl border border-dashed border-border/30 text-center">
              <p className="text-text-secondary italic">No projects found.</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 pb-4">
              {currentProjects.map(project => {
                const isOwner = project._role === 'owner';
                return (
                  <Link to={`/project/${project.id}`} key={project.id} className="relative group h-fit">
                    <div className={`relative p-7 rounded-3xl bg-surface transition-all duration-300 flex flex-col justify-between hover:shadow-2xl hover:-translate-y-1 shadow-md border-none ${
                      isOwner ? 'bg-gradient-to-br from-surface to-primary/[0.03]' : ''
                    }`}>
                      {isOwner && (
                        <div className="absolute left-0 top-8 bottom-8 w-1 rounded-r-full bg-gradient-to-b from-blue-500 to-purple-600 opacity-70"></div>
                      )}
                      <div>
                        <div className="flex justify-between items-start mb-5">
                          <span className={`text-[10px] font-bold px-3 py-1 rounded-lg uppercase tracking-wider ${
                            isOwner ? 'bg-primary/10 text-primary' : 'bg-secondary/5 text-text-secondary'
                          }`}>
                            {isOwner ? 'üëë Master' : 'üõ†Ô∏è Member'}
                          </span>
                          <div className="flex items-center gap-2 opacity-20 group-hover:opacity-100 transition-opacity">
                            {project.is_public ? <FiGlobe size={14} /> : <FiLock size={14} />}
                          </div>
                        </div>
                        <h3 className="text-xl font-bold text-text-main group-hover:text-primary transition-colors truncate mb-3">{project.name}</h3>
                        <p className="text-text-secondary text-xs line-clamp-2 leading-relaxed opacity-70">{project.description || "Project workspace"}</p>
                      </div>
                      <div className="mt-8 pt-5 border-t border-text-secondary/5 flex justify-between items-center text-[10px] text-text-secondary font-bold uppercase">
                        <div className="flex flex-col gap-1.5">
                          <span className={`flex items-center ${isOwner ? 'text-primary/80' : ''}`}>
                            <FiUser className="mr-2 opacity-50" /> {isOwner ? `You` : (project.owner_name || 'Owner')}
                          </span>
                          <span className="flex items-center opacity-40 font-medium">
                            <FiClock className="mr-2" /> {new Date(project.created_at).toLocaleDateString()}
                          </span>
                        </div>
                        <div className="w-8 h-8 rounded-full bg-app/50 flex items-center justify-center group-hover:bg-primary group-hover:text-white transition-all shadow-inner">
                          <FiChevronRight size={16} />
                        </div>
                      </div>
                    </div>
                  </Link>
                );
              })}
            </div>
          )}
        </div>

        {/* SAYFALAMA FOOTER (EKRANIN ALTINA SABƒ∞TLENMƒ∞≈û) */}
        <div className="flex-none pt-6 pb-2">
          <div className="flex justify-between items-center bg-surface p-4 rounded-2xl shadow-lg border-none">
            <span className="text-[11px] text-text-secondary opacity-60 font-medium uppercase tracking-wider">
              Displaying {currentProjects.length} of {filteredProjects.length}
            </span>
            <div className="flex items-center gap-2">
              <button onClick={() => paginate(currentPage - 1)} disabled={currentPage === 1} className="p-2 rounded-xl hover:bg-app disabled:opacity-10 transition-all"><FiChevronLeft size={18} /></button>
              <div className="flex gap-1.5">
                {[...Array(totalPages)].map((_, i) => (
                  <button key={i + 1} onClick={() => paginate(i + 1)} className={`w-9 h-9 rounded-xl text-[11px] font-black transition-all ${currentPage === i + 1 ? 'bg-primary text-white shadow-md scale-105' : 'hover:bg-app text-text-secondary opacity-50'}`}>{i + 1}</button>
                ))}
              </div>
              <button onClick={() => paginate(currentPage + 1)} disabled={currentPage === totalPages} className="p-2 rounded-xl hover:bg-app disabled:opacity-10 transition-all"><FiChevronRight size={18} /></button>
            </div>
          </div>
        </div>
      </div>
      <CreateProjectModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
    </div>
  );
}
export default MyProjectsPage;
