// src/pages/SharedProjectsPage.js

import React, { useState, useEffect, useMemo } from 'react';
import { useProjectContext } from '../context/ProjectContext';
import { FiLoader, FiGrid, FiList, FiChevronLeft, FiChevronRight, FiSearch } from 'react-icons/fi'; 
import SharedProjectCard from '../components/projects/SharedProjectCard';
import api from '../services/api'; // Doğrudan public endpoint'e istek atmak için

function SharedProjectsPage() {
  const [publicProjects, setPublicProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  const [viewMode, setViewMode] = useState(() => {
    return localStorage.getItem('sharedProjectsViewMode') || 'grid';
  });

  const [currentPage, setCurrentPage] = useState(1);
  const projectsPerPage = viewMode === 'grid' ? 6 : 4;

  const changeViewMode = (mode) => {
    setViewMode(mode);
    localStorage.setItem('sharedProjectsViewMode', mode);
  };

  useEffect(() => {
    const fetchPublic = async () => {
      setLoading(true);
      try {
        // Backend'de oluşturduğumuz tüm public projeleri getiren rota
        const response = await api.get('/api/projects/public-explorer');
        setPublicProjects(response.data);
      } catch (err) {
        console.error("Public projects load error:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchPublic();
  }, []);

  // --- ARAMA VE UNİQUE FİLTRELEME ---
  const filteredProjects = useMemo(() => {
    return publicProjects.filter(project => 
      project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()))
    );
  }, [publicProjects, searchTerm]);

  useEffect(() => {
    setCurrentPage(1);
  }, [searchTerm, viewMode]);

  const indexOfLastProject = currentPage * projectsPerPage;
  const indexOfFirstProject = indexOfLastProject - projectsPerPage;
  const currentProjects = filteredProjects.slice(indexOfFirstProject, indexOfLastProject);
  const totalPages = Math.ceil(filteredProjects.length / projectsPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  const nextPage = () => { if (currentPage < totalPages) setCurrentPage(currentPage + 1); };
  const prevPage = () => { if (currentPage > 1) setCurrentPage(currentPage - 1); };

  return (
    <div className="flex flex-col min-h-screen">
      {/* --- HEADER & SEARCH --- */}
      <div className="flex flex-col md:flex-row justify-between items-center mb-8 gap-4">
        <div>
          <h1 className="text-3xl font-bold text-text-main">Public Explorer</h1>
          <p className="text-text-secondary mt-2 text-sm">
            Discover all public projects available on the platform.
          </p>
        </div>

        <div className="flex items-center gap-4 w-full md:w-auto">
          {/* ARAMA ÇUBUĞU */}
          <div className="relative flex-grow md:w-64">
            <FiSearch className="absolute left-3 top-3 text-text-secondary" />
            <input 
              type="text"
              placeholder="Search projects..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full bg-surface border border-border text-text-main text-sm rounded-xl pl-10 pr-4 py-2 outline-none focus:border-primary transition-all shadow-sm"
            />
          </div>

          <div className="bg-surface p-1 rounded-lg border border-border flex shrink-0">
            <button
              onClick={() => changeViewMode('grid')}
              className={`p-2 rounded-md transition-all ${
                viewMode === 'grid' ? 'bg-primary text-white shadow' : 'text-text-secondary hover:text-text-main hover:bg-surface-hover'
              }`}
            >
              <FiGrid size={18} />
            </button>
            <button
              onClick={() => changeViewMode('list')}
              className={`p-2 rounded-md transition-all ${
                viewMode === 'list' ? 'bg-primary text-white shadow' : 'text-text-secondary hover:text-text-main hover:bg-surface-hover'
              }`}
            >
              <FiList size={18} />
            </button>
          </div>
        </div>
      </div>

      {/* --- CONTENT --- */}
      {loading ? (
        <div className="flex flex-col justify-center items-center p-20">
          <FiLoader className="animate-spin text-primary" size={40} />
          <span className="ml-4 mt-4 text-xl text-text-secondary">Loading Public Projects...</span>
        </div>
      ) : filteredProjects.length === 0 ? (
        <div className="bg-surface/50 p-20 rounded-2xl border border-dashed border-border text-center">
          <p className="text-text-secondary">No public projects found matching your criteria.</p>
        </div>
      ) : (
        <div className="flex flex-col flex-grow">
          <div className="flex-grow">
            {viewMode === 'grid' ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {currentProjects.map(project => (
                  <SharedProjectCard key={project.id} project={project} viewMode="grid" />
                ))}
              </div>
            ) : (
              <div className="flex flex-col space-y-3">
                {currentProjects.map(project => (
                  <SharedProjectCard key={project.id} project={project} viewMode="list" />
                ))}
              </div>
            )}
          </div>

          {/* === PAGINATION BAR === */}
          {filteredProjects.length > projectsPerPage && (
            <div className="flex justify-center items-center mt-10 pt-4 border-t border-border">
              <button
                onClick={prevPage}
                disabled={currentPage === 1}
                className={`p-2 rounded-md border border-border ${
                  currentPage === 1 
                    ? 'text-text-secondary cursor-not-allowed bg-surface/50 opacity-30' 
                    : 'text-text-main bg-surface hover:bg-surface-hover hover:border-primary'
                }`}
              >
                <FiChevronLeft size={20} />
              </button>

              <div className="flex space-x-1 mx-2">
                {Array.from({ length: totalPages }, (_, i) => i + 1).map((number) => (
                  <button
                    key={number}
                    onClick={() => paginate(number)}
                    className={`px-3 py-1 rounded-md text-sm font-medium transition-colors ${
                      currentPage === number
                        ? 'bg-primary text-white shadow-lg'
                        : 'bg-surface text-text-secondary border border-border hover:bg-surface-hover hover:text-text-main'
                    }`}
                  >
                    {number}
                  </button>
                ))}
              </div>

              <button
                onClick={nextPage}
                disabled={currentPage === totalPages}
                className={`p-2 rounded-md border border-border ${
                  currentPage === totalPages 
                    ? 'text-text-secondary cursor-not-allowed bg-surface/50 opacity-30' 
                    : 'text-text-main bg-surface hover:bg-surface-hover hover:border-primary'
                }`}
              >
                <FiChevronRight size={20} />
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default SharedProjectsPage;
