// src/pages/MyProjectsPage.js

import React, { useState, useEffect, useMemo } from 'react';
import { FiLoader, FiSearch, FiPlus, FiChevronLeft, FiChevronRight, FiUser, FiClock, FiGlobe, FiLock } from 'react-icons/fi';
import { Link } from 'react-router-dom';
import CreateProjectModal from '../components/projects/CreateProjectModal';

function MyProjectsPage({ user, myProjects = [], sharedProjects = [], loading }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [ownershipFilter, setOwnershipFilter] = useState('all');
  const [visibilityFilter, setVisibilityFilter] = useState('all');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const projectsPerPage = 6;

  const filteredProjects = useMemo(() => {
    // undefined kontrolü yaparak map hatasını engelliyoruz
    const safeOwned = Array.isArray(myProjects) ? myProjects : [];
    const safeShared = Array.isArray(sharedProjects) ? sharedProjects : [];

    const owned = safeOwned.map(p => ({ ...p, _role: 'owner' }));
    const shared = safeShared.map(p => ({ ...p, _role: 'member' }));
    const all = [...owned, ...shared];

    return all.filter(project => {
      const matchesSearch = project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                            (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()));
      const matchesOwnership = ownershipFilter === 'all' || project._role === ownershipFilter;
      const matchesVisibility = visibilityFilter === 'all' || 
                               (visibilityFilter === 'public' ? project.is_public : !project.is_public);
      
      return matchesSearch && matchesOwnership && matchesVisibility;
    });
  }, [myProjects, sharedProjects, searchTerm, ownershipFilter, visibilityFilter]);

  const totalPages = Math.max(Math.ceil(filteredProjects.length / projectsPerPage), 1);
  const currentProjects = filteredProjects.slice((currentPage - 1) * projectsPerPage, currentPage * projectsPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  useEffect(() => { setCurrentPage(1); }, [searchTerm, ownershipFilter, visibilityFilter]);

  return (
    <div className="flex flex-col min-h-screen p-6 bg-app transition-colors duration-300">
      <div className="max-w-7xl mx-auto w-full flex-grow flex flex-col">
        
        <div className="flex justify-between items-center mb-10">
          <h1 className="text-3xl font-black text-text-main tracking-tight">My Projects</h1>
          <button 
            onClick={() => setIsModalOpen(true)} 
            className="bg-primary hover:opacity-90 text-white font-semibold py-2.5 px-6 rounded-xl shadow-lg transition-all transform hover:scale-105 active:scale-95 flex items-center text-sm border-none"
          >
            <FiPlus className="mr-2" /> New Project
          </button>
        </div>

        {/* SEARCH AND FILTERS */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 bg-surface p-4 rounded-2xl border border-border shadow-sm mb-10">
          <div className="relative col-span-1 md:col-span-2">
            <FiSearch className="absolute left-3 top-3.5 text-text-secondary" />
            <input
              type="text"
              placeholder="Search by name or description..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full bg-app border border-border text-text-main text-sm rounded-xl pl-10 py-3 outline-none focus:border-primary transition-all"
            />
          </div>
          
          <select 
            value={ownershipFilter} 
            onChange={(e) => setOwnershipFilter(e.target.value)} 
            className="w-full bg-app border border-border text-text-main text-sm rounded-xl px-4 py-3 outline-none focus:border-primary cursor-pointer appearance-none"
          >
            <option value="all">All Roles</option>
            <option value="owner">Owner</option>
            <option value="member">Member</option>
          </select>

          <select 
            value={visibilityFilter} 
            onChange={(e) => setVisibilityFilter(e.target.value)} 
            className="w-full bg-app border border-border text-text-main text-sm rounded-xl px-4 py-3 outline-none focus:border-primary cursor-pointer appearance-none"
          >
            <option value="all">All Visibility</option>
            <option value="public">Public</option>
            <option value="private">Private</option>
          </select>
        </div>

        <div className="flex-grow">
          {loading ? (
            <div className="flex justify-center p-20"><FiLoader className="animate-spin text-primary/40" size={40} /></div>
          ) : filteredProjects.length === 0 ? (
            <div className="p-20 text-center bg-surface/20 rounded-2xl border border-dashed border-border/40">
              <p className="text-text-secondary text-sm font-medium">No projects found matching the criteria.</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
              {currentProjects.map(project => {
                const isOwner = project._role === 'owner';
                return (
                  <Link to={`/project/${project.id}`} key={project.id} className="relative group">
                    <div className="relative p-8 rounded-2xl bg-surface transition-all duration-300 h-full flex flex-col justify-between hover:shadow-xl shadow-sm border-none dark:bg-surface-dark">
                      {isOwner && (
                        <div className="absolute left-0 top-10 bottom-10 w-1 rounded-r-full bg-gradient-to-b from-blue-500 to-purple-600 opacity-80"></div>
                      )}
                      <div>
                        <div className="flex justify-between items-start mb-6">
                          <span className={`text-[10px] font-bold px-3 py-1 rounded-md uppercase tracking-widest ${
                            isOwner ? 'bg-primary/10 text-primary' : 'bg-text-secondary/5 text-text-secondary'
                          }`}>
                            {isOwner ? 'Owner' : 'Member'}
                          </span>
                          <div className="flex items-center gap-2 opacity-20">
                            {project.is_public ? <FiGlobe size={14} /> : <FiLock size={14} />}
                          </div>
                        </div>
                        <h3 className="text-xl font-bold text-text-main group-hover:text-primary transition-colors truncate mb-3">{project.name}</h3>
                        <p className="text-xs text-text-secondary/70 line-clamp-2 leading-relaxed">{project.description || "Project workspace"}</p>
                      </div>
                      <div className="mt-10 pt-6 border-t border-text-secondary/5 flex justify-between items-center text-[10px] text-text-secondary font-bold uppercase tracking-tighter">
                        <div className="flex flex-col gap-2">
                          <span className={`flex items-center ${isOwner ? 'text-primary/70' : ''}`}>
                            <FiUser className="mr-2" /> {isOwner ? `You` : (project.owner_name || 'Owner')}
                          </span>
                          <span className="flex items-center opacity-40 font-medium">
                            <FiClock className="mr-2" /> {new Date(project.created_at).toLocaleDateString()}
                          </span>
                        </div>
                        <div className="flex items-center text-primary font-black uppercase tracking-widest">View</div>
                      </div>
                    </div>
                  </Link>
                );
              })}
            </div>
          )}
        </div>

        {/* PAGINATION */}
        <div className="mt-auto pt-6 pb-10">
          <div className="flex justify-between items-center bg-surface p-4 rounded-xl shadow-sm border border-border">
            <span className="text-[10px] text-text-secondary font-semibold uppercase tracking-widest opacity-60">
              Showing {currentProjects.length} of {filteredProjects.length}
            </span>
            <div className="flex items-center gap-3">
              <button onClick={() => paginate(currentPage - 1)} disabled={currentPage === 1} className="p-2 rounded-lg hover:bg-app disabled:opacity-10 transition-all text-text-main border border-border"><FiChevronLeft size={18} /></button>
              <div className="flex gap-2">
                {[...Array(totalPages)].map((_, i) => (
                  <button key={i + 1} onClick={() => paginate(i + 1)} className={`w-9 h-9 rounded-lg text-[10px] font-black transition-all ${currentPage === i + 1 ? 'bg-primary text-white shadow-sm' : 'hover:bg-app text-text-secondary border border-border'}`}>{i + 1}</button>
                ))}
              </div>
              <button onClick={() => paginate(currentPage + 1)} disabled={currentPage === totalPages} className="p-2 rounded-lg hover:bg-app disabled:opacity-10 transition-all text-text-main border border-border"><FiChevronRight size={18} /></button>
            </div>
          </div>
        </div>
      </div>
      <CreateProjectModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
    </div>
  );
}

export default MyProjectsPage;
