// src/components/projects/ProjectFiles.js
import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import {
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader,
  FiFileText, FiX, FiEye, FiCheckSquare, FiSquare,
  FiChevronLeft, FiChevronRight, FiExternalLink, FiAirplay
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();

  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  /* ---------------- FETCH FILES ---------------- */
  useEffect(() => {
    if (!currentProject) return;
    setLoading(true);
    fetchFiles(currentProject.id)
      .then(data => setFiles(Array.isArray(data) ? data : []))
      .finally(() => setLoading(false));
  }, [currentProject]);

  /* ---------------- KEYBOARD NAV ---------------- */
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!previewFile) return;
      if (e.key === 'ArrowRight') handleNextPrev('next');
      if (e.key === 'ArrowLeft') handleNextPrev('prev');
      if (e.key === 'Escape') setPreviewFile(null);
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [previewFile, files]);

  /* ---------------- HELPERS ---------------- */
  const getFileUrl = (filePath) => {
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  const getFileType = (filename) => {
    if (!filename) return 'other';
    const ext = filename.split('.').pop().toLowerCase();

    if (['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg'].includes(ext)) return 'image';
    if (ext === 'pdf') return 'pdf';
    if (['doc', 'docx'].includes(ext)) return 'word';
    if (['ppt', 'pptx'].includes(ext)) return 'powerpoint';
    if (['txt', 'md', 'json', 'js', 'css', 'html', 'log'].includes(ext)) return 'text';
    return 'other';
  };

  const getPreviewUrl = (file) => {
    const url = getFileUrl(file.file_path);
    const type = getFileType(file.filename);

    if (type === 'image' || type === 'pdf' || type === 'text') return url;

    if (type === 'word' || type === 'powerpoint') {
      return `https://view.officeapps.live.com/op/embed.aspx?src=${encodeURIComponent(url)}`;
    }

    return url;
  };

  /* ---------------- ACTIONS ---------------- */
  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (!selectedFiles.length) return;

    setUploading(true);
    try {
      const results = [];
      for (const file of selectedFiles) {
        const res = await uploadFile(currentProject.id, file);
        if (res?.id) results.push(res);
      }
      setFiles(prev => [...results, ...prev]);
    } finally {
      setUploading(false);
      e.target.value = null;
    }
  };

  const handleDeleteSingle = async (id) => {
    if (!window.confirm("Delete this file?")) return;
    await deleteFile(currentProject.id, id);
    setFiles(prev => prev.filter(f => f.id !== id));
    setPreviewFile(null);
  };

  const handleNextPrev = (dir) => {
    const idx = files.findIndex(f => f.id === previewFile.id);
    const next = dir === 'next'
      ? (idx + 1) % files.length
      : (idx - 1 + files.length) % files.length;
    setPreviewFile(files[next]);
  };

  /* ================= RENDER ================= */
  return (
    <div className="space-y-8 w-full">

      {/* HEADER */}
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-black">Project Files</h2>
        {canEdit && (
          <div className="relative">
            <input
              type="file"
              multiple
              onChange={handleFileUpload}
              accept=".jpg,.jpeg,.png,.pdf,.doc,.docx,.ppt,.pptx,.txt,.md"
              className="absolute inset-0 opacity-0 cursor-pointer"
            />
            <button className="px-6 py-2 bg-primary text-white rounded-xl font-bold flex items-center gap-2">
              {uploading ? <FiLoader className="animate-spin" /> : <FiUpload />}
              Upload
            </button>
          </div>
        )}
      </div>

      {/* FILE LIST */}
      <div className="bg-surface rounded-2xl border border-border overflow-hidden">
        <ul className="divide-y divide-border">
          {files.map(file => (
            <li
              key={file.id}
              onClick={() => setPreviewFile(file)}
              className="flex items-center p-4 hover:bg-app cursor-pointer"
            >
              <FiFile className="mr-4" />
              <span className="font-bold truncate">{file.filename}</span>
              <FiEye className="ml-auto opacity-50" />
            </li>
          ))}
        </ul>
      </div>

      {/* ================= FULLSCREEN PREVIEW ================= */}
      {previewFile && (
        <div className="fixed inset-0 z-[99999] w-screen h-[100dvh] bg-black flex flex-col overflow-hidden">

          {/* TOP BAR */}
          <div className="h-16 flex-none flex items-center justify-between px-6 bg-black/80 border-b border-white/10">
            <div className="flex items-center gap-4 text-white">
              <button onClick={() => setPreviewFile(null)}>
                <FiX size={28} />
              </button>
              <span className="font-bold truncate max-w-md">
                {previewFile.filename}
              </span>
            </div>

            <div className="flex gap-3">
              <a
                href={getFileUrl(previewFile.file_path)}
                target="_blank"
                rel="noopener noreferrer"
                className="px-4 py-2 bg-white/10 rounded-lg text-white text-xs font-bold flex items-center gap-2"
              >
                <FiExternalLink /> Open
              </a>
              <a
                href={getFileUrl(previewFile.file_path)}
                download
                className="px-4 py-2 bg-primary rounded-lg text-white text-xs font-black flex items-center gap-2"
              >
                <FiDownload /> Download
              </a>
              {canEdit && (
                <button
                  onClick={() => handleDeleteSingle(previewFile.id)}
                  className="p-2 bg-red-600 rounded-lg text-white"
                >
                  <FiTrash2 />
                </button>
              )}
            </div>
          </div>

          {/* CONTENT */}
          <div className="relative flex-1 bg-[#0a0a0a] overflow-hidden flex items-center justify-center">

            <button
              onClick={() => handleNextPrev('prev')}
              className="absolute left-6 text-white/30 hover:text-white"
            >
              <FiChevronLeft size={56} />
            </button>

            <button
              onClick={() => handleNextPrev('next')}
              className="absolute right-6 text-white/30 hover:text-white"
            >
              <FiChevronRight size={56} />
            </button>

            {getFileType(previewFile.filename) === 'image' ? (
              <img
                src={getFileUrl(previewFile.file_path)}
                className="w-full h-full object-contain"
                alt=""
              />
            ) : (
              <iframe
                src={getPreviewUrl(previewFile)}
                className="w-full h-full border-none bg-white"
                allowFullScreen
                loading="lazy"
                title="preview"
              />
            )}
          </div>
        </div>
      )}
    </div>
  );
}
