import React, { useState, useEffect, useRef } from 'react';
import api from '../services/api';
import { 
  FiUserPlus, FiMoreVertical, FiEdit2, FiTrash2, 
  FiChevronLeft, FiChevronRight, FiX, FiSearch,
  FiBox, FiUsers, FiExternalLink, FiLoader
} from 'react-icons/fi';

function Users() {
  const [activeTab, setActiveTab] = useState('users'); 
  const [users, setUsers] = useState([]);
  const [projects, setProjects] = useState([]); 
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(7);
  const [searchTerm, setSearchTerm] = useState(""); 
  const [openMenuId, setOpenMenuId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  
  const [formData, setFormData] = useState({ username: '', email: '', password: '', role: 'student' });
  const menuRef = useRef(null);

  const toggleMenu = (id, e) => {
    e.stopPropagation();
    setOpenMenuId(openMenuId === id ? null : id);
  };

  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) setOpenMenuId(null);
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  useEffect(() => { 
    if (activeTab === 'users') fetchUsers();
    else fetchAllProjects();
  }, [activeTab]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await api.get('/api/auth/users');
      setUsers(Array.isArray(response.data) ? response.data : []);
    } catch (err) { console.error(err); } 
    finally { setLoading(false); }
  };

  const fetchAllProjects = async () => {
    setLoading(true);
    try {
      const response = await api.get('/api/projects/admin/all-projects'); 
      setProjects(Array.isArray(response.data) ? response.data : []);
    } catch (err) { 
      console.error(err);
      setProjects([]);
    } finally {
      setLoading(false);
    }
  };

  const filteredData = activeTab === 'users' 
    ? users.filter(u => (u.username || "").toLowerCase().includes(searchTerm.toLowerCase()))
    : projects.filter(p => (p.name || "").toLowerCase().includes(searchTerm.toLowerCase()));

  const totalPages = Math.max(Math.ceil(filteredData.length / itemsPerPage), 1);
  const currentItems = filteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const openEditModal = (user) => {
      setSelectedItem(user);
      setFormData({ username: user.username, email: user.email, password: '', role: user.role || 'student' });
      setShowModal(true);
      setOpenMenuId(null);
  };

  const handleDeleteUser = async (user) => {
    if (!window.confirm(`${user.username} silinsin mi?`)) return;
    try {
      await api.delete(`/api/auth/users/${user.id}`);
      fetchUsers();
    } catch (err) { alert("Hata!"); }
  };

  const handleDeleteProject = async (project) => {
    if (!window.confirm(`${project.name} silinsin mi?`)) return;
    try {
      await api.delete(`/api/projects/${project.id}`);
      fetchAllProjects();
    } catch (err) { alert("Hata!"); }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    try {
        if (selectedItem) await api.put(`/api/auth/users/${selectedItem.id}`, formData);
        else await api.post('/api/auth/register', formData);
        fetchUsers();
        setShowModal(false);
    } catch (err) { alert("Hata!"); }
    finally { setIsSubmitting(false); }
  };

  return (
    <div className="flex flex-col h-[calc(100vh-140px)] container mx-auto max-w-6xl px-4 py-4 overflow-hidden">
      <div className="flex items-center space-x-1 bg-surface p-1 rounded-xl border border-border w-fit mb-6 shadow-sm">
        <button onClick={() => { setActiveTab('users'); setCurrentPage(1); }} className={`flex items-center px-6 py-2 rounded-lg text-sm font-bold transition-all ${activeTab === 'users' ? 'bg-primary text-white shadow-md' : 'text-text-secondary hover:bg-surface-hover'}`}><FiUsers className="mr-2" /> Users</button>
        <button onClick={() => { setActiveTab('projects'); setCurrentPage(1); }} className={`flex items-center px-6 py-2 rounded-lg text-sm font-bold transition-all ${activeTab === 'projects' ? 'bg-primary text-white shadow-md' : 'text-text-secondary hover:bg-surface-hover'}`}><FiBox className="mr-2" /> Projects</button>
      </div>

      <div className="flex-none flex flex-col md:flex-row justify-between items-center mb-4 gap-4">
        <div className="relative flex-1 md:w-80 w-full">
          <FiSearch className="absolute left-3 top-2.5 text-text-secondary" />
          <input type="text" placeholder="Search..." value={searchTerm} onChange={(e) => { setSearchTerm(e.target.value); setCurrentPage(1); }} className="w-full bg-surface border border-border text-text-main text-sm rounded-lg pl-10 pr-4 py-2 focus:border-primary outline-none" />
        </div>
        {activeTab === 'users' && (
          <button onClick={() => { setSelectedItem(null); setFormData({username:'', email:'', password:'', role:'student'}); setShowModal(true); }} className="px-4 py-2 bg-primary text-white rounded-lg font-bold text-sm border-none shadow-md flex items-center"><FiUserPlus className="mr-2" /> Add User</button>
        )}
      </div>

      <div className="flex-grow bg-surface rounded-xl border border-border shadow-xl overflow-hidden flex flex-col relative" ref={menuRef}>
        <div className="overflow-x-auto flex-1 custom-scrollbar">
          {loading ? (
            <div className="flex justify-center p-20"><FiLoader className="animate-spin text-primary" size={40} /></div>
          ) : (
            <table className="w-full text-left text-text-secondary">
              <thead className="bg-app/80 text-[11px] uppercase font-black sticky top-0 z-10 border-b border-border backdrop-blur-sm">
                <tr>
                  <th className="px-6 py-4">{activeTab === 'users' ? 'User' : 'Project'}</th>
                  <th className="px-6 py-4">{activeTab === 'users' ? 'Role' : 'Owner'}</th>
                  <th className="px-6 py-4 text-right">Date</th>
                  <th className="px-6 py-4 w-10"></th>
                </tr>
              </thead>
              <tbody className="divide-y divide-border text-sm">
                {currentItems.map((item) => (
                  <tr key={item.id} className="hover:bg-surface-hover transition-colors">
                    <td className="px-6 py-3 font-bold text-text-main">{activeTab === 'users' ? item.username : item.name}</td>
                    <td className="px-6 py-3">{activeTab === 'users' ? <span className="text-[10px] font-bold px-2 py-0.5 rounded border bg-blue-500/10 text-blue-500">{item.role?.toUpperCase()}</span> : item.owner_name}</td>
                    <td className="px-6 py-3 text-right opacity-40">{new Date(item.updated_at || item.created_at).toLocaleDateString()}</td>
                    <td className="px-6 py-3 relative">
                      <button onClick={(e) => toggleMenu(item.id, e)} className="p-2 rounded-lg hover:bg-app text-text-secondary transition-colors"><FiMoreVertical size={16} /></button>
                      {openMenuId === item.id && (
                        <div className="absolute right-8 top-2 w-40 bg-surface border border-border rounded-xl shadow-2xl z-50 py-1 overflow-hidden">
                          {activeTab === 'users' ? (
                            <button onClick={() => openEditModal(item)} className="w-full text-left px-4 py-2 text-xs font-bold hover:bg-surface-hover border-none flex items-center"><FiEdit2 className="mr-2"/> EDIT</button>
                          ) : (
                            <button onClick={() => window.open(`/project/${item.id}`, '_blank')} className="w-full text-left px-4 py-2 text-xs font-bold hover:bg-surface-hover border-none flex items-center"><FiExternalLink className="mr-2"/> VIEW</button>
                          )}
                          <button onClick={() => activeTab === 'users' ? handleDeleteUser(item) : handleDeleteProject(item)} className="w-full text-left px-4 py-2 text-xs font-bold text-red-500 hover:bg-red-500/10 border-none flex items-center"><FiTrash2 className="mr-2"/> DELETE</button>
                        </div>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
      {showModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center bg-black/80 backdrop-blur-sm">
          <div className="bg-surface border border-border rounded-2xl w-full max-w-md p-8 relative">
            <button onClick={() => setShowModal(false)} className="absolute top-6 right-6 bg-transparent border-none cursor-pointer"><FiX size={20} /></button>
            <h2 className="text-xl font-black mb-6 uppercase tracking-tight">{selectedItem ? 'Update User' : 'New User'}</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input name="username" required value={formData.username} onChange={(e) => setFormData({...formData, username: e.target.value})} className="w-full py-3 px-4 text-sm bg-app border border-border rounded-xl outline-none" placeholder="Username"/>
              <input type="email" name="email" required value={formData.email} onChange={(e) => setFormData({...formData, email: e.target.value})} className="w-full py-3 px-4 text-sm bg-app border border-border rounded-xl outline-none" placeholder="Email"/>
              <input type="password" name="password" required={!selectedItem} value={formData.password} onChange={(e) => setFormData({...formData, password: e.target.value})} className="w-full py-3 px-4 text-sm bg-app border border-border rounded-xl outline-none" placeholder="Password"/>
              <button type="submit" disabled={isSubmitting} className="w-full bg-primary text-white font-black rounded-xl py-3 mt-4 border-none cursor-pointer uppercase tracking-widest text-xs transition-opacity hover:opacity-90">{isSubmitting ? 'Saving...' : 'Save'}</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Users;
