// src/pages/MyProjectsPage.js
import React, { useState, useEffect, useMemo } from 'react';
import CreateProjectModal from '../components/projects/CreateProjectModal';
import { Link } from 'react-router-dom';
import { useProjectContext } from '../context/ProjectContext';
import { useAuth } from '../context/AuthContext';
import { 
  FiLoader, FiPlus, FiGrid, FiList, FiSearch, FiGlobe, FiLock, FiChevronLeft, FiChevronRight, FiUser, FiClock 
} from 'react-icons/fi';

function MyProjectsPage() {
  const { user } = useAuth();
  const { myProjects, sharedProjects, loading, fetchMyProjects, fetchSharedProjects } = useProjectContext();
  
  const [isModalOpen, setIsModalOpen] = useState(false); 
  const [viewMode, setViewMode] = useState('grid'); 
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  
  // İSTEDİĞİNİZ 2 FİLTRE
  const [ownershipFilter, setOwnershipFilter] = useState('all'); // all, owner, member
  const [visibilityFilter, setVisibilityFilter] = useState('all'); // all, public, private

  const projectsPerPage = viewMode === 'grid' ? 6 : 5;

  useEffect(() => {
    fetchMyProjects();
    fetchSharedProjects();
  }, []);

  // --- ÜYELİK KONTROLÜ VE VERİ BİRLEŞTİRME ---
 const joinedProjects = useMemo(() => {
  if (!user || !user.id) return [];

  // 1. Sahibi olduğum projeler (Tip uyuşmazlığını önlemek için Number kullanıyoruz)
  const owned = myProjects.map(p => ({ 
    ...p, 
    _role: 'owner' 
  }));

  // 2. Üyesi olduğum projeler
  const shared = sharedProjects.map(p => ({ 
    ...p, 
    _role: 'member' 
  }));

  // Filtreleme yapmadan önce iki listeyi birleştiriyoruz
  const all = [...owned, ...shared];

  // Mükerrer kayıtları (hem owner hem member gibi görünenleri) ID üzerinden temizle
  const unique = [];
  const map = new Map();
  for (const item of all) {
    if(!map.has(item.id)){
        map.set(item.id, true);
        unique.push(item);
    }
  }
  return unique;
}, [myProjects, sharedProjects, user]);

  // --- FİLTRELEME MANTIĞI ---
  const filteredProjects = useMemo(() => {
    return joinedProjects.filter(project => {
      // Arama Filtresi
      const matchesSearch = project.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          (project.description && project.description.toLowerCase().includes(searchTerm.toLowerCase()));
      
      // Filtre 1: Sahiplik Durumu (Benim kurduklarım / Katıldıklarım)
      const matchesOwnership = ownershipFilter === 'all' ? true : project._role === ownershipFilter;
      
      // Filtre 2: Görünürlük Durumu (Public / Private)
      const matchesVisibility = visibilityFilter === 'all' ? true : 
                               visibilityFilter === 'public' ? project.is_public === true : 
                               project.is_public === false;

      return matchesSearch && matchesOwnership && matchesVisibility;
    });
  }, [joinedProjects, searchTerm, ownershipFilter, visibilityFilter]);

  // Sayfalama
  const currentProjects = filteredProjects.slice((currentPage - 1) * projectsPerPage, currentPage * projectsPerPage);
  const totalPages = Math.max(Math.ceil(filteredProjects.length / projectsPerPage), 1);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  useEffect(() => { setCurrentPage(1); }, [searchTerm, ownershipFilter, visibilityFilter]);

  return (
    <div className="flex flex-col min-h-[calc(100vh-140px)] p-6 bg-app transition-colors duration-300">
      <div className="max-w-7xl mx-auto w-full flex-grow flex flex-col">
        
        {/* ÜST PANEL */}
        <div className="flex-none mb-10">
          <div className="flex justify-between items-center mb-8">
            <h1 className="text-2xl font-semibold text-text-main tracking-tight dark:text-white/90">Workspaces</h1>
            <button 
              onClick={() => setIsModalOpen(true)} 
              className="bg-primary hover:opacity-90 text-white font-medium py-2 px-5 rounded-lg shadow-sm transition-all flex items-center text-sm"
            >
              <FiPlus className="mr-2" /> New Project
            </button>
          </div>

          {/* FİLTRE PANELİ - Daha sade ve border-free görünüm */}
          <div className="flex flex-wrap gap-4 items-center bg-surface/40 dark:bg-surface-dark/40 p-2 rounded-xl backdrop-blur-sm">
            <div className="relative flex-grow max-w-md">
              <FiSearch className="absolute left-3 top-2.5 text-text-secondary opacity-40" />
              <input
                type="text"
                placeholder="Find anything..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full bg-transparent border-none text-text-main text-sm rounded-lg pl-10 py-2 focus:ring-1 focus:ring-primary/30 outline-none"
              />
            </div>
            
            <div className="h-4 w-[1px] bg-border/40 hidden md:block"></div>

            <select 
              value={ownershipFilter} 
              onChange={(e) => setOwnershipFilter(e.target.value)} 
              className="bg-transparent text-text-secondary text-xs font-medium cursor-pointer outline-none hover:text-text-main transition-colors"
            >
              <option value="all">All Roles</option>
              <option value="owner">Owned by me</option>
              <option value="member">Shared with me</option>
            </select>

            <select 
              value={visibilityFilter} 
              onChange={(e) => setVisibilityFilter(e.target.value)} 
              className="bg-transparent text-text-secondary text-xs font-medium cursor-pointer outline-none hover:text-text-main transition-colors"
            >
              <option value="all">Privacy: All</option>
              <option value="public">Public</option>
              <option value="private">Private</option>
            </select>
          </div>
        </div>

        {/* PROJE LİSTESİ */}
        <div className="flex-grow">
          {loading ? (
            <div className="flex justify-center p-20"><FiLoader className="animate-spin text-primary/40" size={40} /></div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {currentProjects.map(project => {
                const isOwner = project._role === 'owner';

                return (
                  <Link to={`/project/${project.id}`} key={project.id} className="group relative">
                    {/* Arka Plan Efekti - Sadece Hover'da çıkar */}
                    <div className="absolute -inset-2 bg-primary/5 rounded-3xl opacity-0 group-hover:opacity-100 transition-all duration-500"></div>
                    
                    <div className={`relative p-6 rounded-2xl transition-all duration-300 h-full flex flex-col justify-between ${
                      isOwner 
                        ? 'bg-surface shadow-[0_8px_30px_rgb(0,0,0,0.04)] dark:bg-surface-dark dark:shadow-[0_8px_30px_rgb(0,0,0,0.2)]' 
                        : 'bg-transparent border border-border/40'
                    }`}>
                      
                      <div>
                        <div className="flex justify-between items-center mb-4">
                          {/* Owner ise küçük bir nokta (dot) ve text ağırlığı ile ayırıyoruz */}
                          <div className="flex items-center gap-2">
                            {isOwner && <div className="w-1.5 h-1.5 rounded-full bg-primary shadow-[0_0_8px_rgba(var(--primary-rgb),0.6)]"></div>}
                            <span className={`text-[10px] tracking-widest uppercase font-bold ${isOwner ? 'text-text-main' : 'text-text-secondary/60'}`}>
                              {isOwner ? 'Master' : 'Guest'}
                            </span>
                          </div>
                          {project.is_public ? <FiGlobe size={14} className="opacity-30" /> : <FiLock size={14} className="opacity-30" />}
                        </div>

                        <h3 className={`text-base mb-2 transition-colors ${isOwner ? 'font-semibold text-text-main group-hover:text-primary' : 'font-medium text-text-secondary group-hover:text-text-main'}`}>
                          {project.name}
                        </h3>
                        <p className="text-xs text-text-secondary/70 line-clamp-2 font-normal leading-relaxed">
                          {project.description || "Project workspace"}
                        </p>
                      </div>

                      <div className="mt-8 flex items-center justify-between">
                        <div className="flex -space-x-2">
                           {/* Sahibi resmi yerine harf ikonu (Placeholder) */}
                           <div className={`w-7 h-7 rounded-full border-2 border-surface flex items-center justify-center text-[10px] font-bold ${isOwner ? 'bg-primary text-white' : 'bg-border text-text-secondary'}`}>
                              {isOwner ? user?.username?.charAt(0).toUpperCase() : (project.owner_name?.charAt(0).toUpperCase() || 'C')}
                           </div>
                        </div>
                        
                        <div className="flex items-center text-[10px] font-medium text-text-secondary/50 group-hover:text-primary transition-colors">
                           <span className="mr-2 uppercase tracking-tighter">{isOwner ? 'Manage' : 'View'}</span>
                           <FiChevronRight size={14} className="transform group-hover:translate-x-1 transition-transform" />
                        </div>
                      </div>
                    </div>
                  </Link>
                );
              })}
            </div>
          )}
        </div>

        {/* SAYFALAMA - Minimalist */}
        <div className="flex-none mt-16 py-6 border-t border-border/20">
          <div className="flex justify-center items-center gap-8">
            <button 
              onClick={() => paginate(currentPage - 1)} 
              disabled={currentPage === 1} 
              className="text-text-secondary hover:text-primary disabled:opacity-10 transition-colors"
            >
              <FiChevronLeft size={24} />
            </button>
            
            <div className="flex gap-4">
              {[...Array(totalPages)].map((_, i) => (
                <button 
                  key={i + 1} 
                  onClick={() => paginate(i + 1)} 
                  className={`text-xs font-semibold transition-all ${currentPage === i + 1 ? 'text-primary' : 'text-text-secondary/40 hover:text-text-secondary'}`}
                >
                  {String(i + 1).padStart(2, '0')}
                </button>
              ))}
            </div>

            <button 
              onClick={() => paginate(currentPage + 1)} 
              disabled={currentPage === totalPages} 
              className="text-text-secondary hover:text-primary disabled:opacity-10 transition-colors"
            >
              <FiChevronRight size={24} />
            </button>
          </div>
        </div>

      </div>
      <CreateProjectModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} />
    </div>
  );
}
export default MyProjectsPage;
