import React from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { useAuth } from '../../context/AuthContext';
import { FiCalendar, FiUsers, FiUser, FiInfo, FiLock, FiGlobe, FiActivity, FiCheckCircle } from 'react-icons/fi';

export default function RightSidebar() {
  const { currentProject, currentMembers, loading, dashboardTasks } = useProjectContext();
  const { user } = useAuth();

  // Helper: Date Formatter
  const formatDate = (dateString) => {
    if (!dateString) return '-';
    return new Date(dateString).toLocaleDateString('en-US', { day: 'numeric', month: 'long', year: 'numeric' });
  };

  if (loading) return <div className="w-80 bg-gray-900 p-6 text-gray-400 border-l border-gray-800 hidden lg:block">Loading...</div>;

  // =========================================================
  // CASE 1: DASHBOARD VIEW (NO PROJECT SELECTED)
  // =========================================================
  if (!currentProject) {
    return (
      <div className="w-80 bg-gray-900 border-l border-gray-800 text-white flex flex-col h-full hidden lg:flex">
        {/* User Profile Summary */}
        <div className="p-8 border-b border-gray-800 flex flex-col items-center text-center">
            <div className="w-20 h-20 rounded-full bg-gradient-to-br from-blue-600 to-purple-600 flex items-center justify-center text-2xl font-bold mb-4 shadow-lg">
                {user?.username ? user.username.substring(0, 2).toUpperCase() : <FiUser />}
            </div>
            <h2 className="text-xl font-bold">{user?.username || 'Guest'}</h2>
            <p className="text-sm text-gray-400 mt-1 capitalize">{user?.role || 'Member'}</p>
            <p className="text-xs text-gray-500 mt-2">{user?.email}</p>
        </div>

        {/* Quick Stats / Global Info */}
        <div className="p-6">
            <h3 className="text-xs font-bold text-gray-500 uppercase mb-4 flex items-center gap-2">
                <FiActivity /> Your Activity
            </h3>
            <div className="bg-gray-800/50 rounded-lg p-4 border border-gray-700">
                <div className="flex justify-between items-center mb-2">
                    <span className="text-sm text-gray-300">Pending Tasks</span>
                    <span className="text-lg font-bold text-blue-400">{dashboardTasks?.length || 0}</span>
                </div>
                <div className="w-full bg-gray-700 h-1.5 rounded-full">
                    <div className="bg-blue-500 h-1.5 rounded-full" style={{ width: '60%' }}></div>
                </div>
            </div>
        </div>

        <div className="mt-auto p-6 border-t border-gray-800 text-center">
             <p className="text-xs text-gray-600">Gazoz Collaboration System v1.0</p>
        </div>
      </div>
    );
  }

  // =========================================================
  // CASE 2: PROJECT VIEW (PROJECT SELECTED)
  // =========================================================
  return (
    <div className="w-80 bg-gray-900 border-l border-gray-800 text-white flex flex-col h-full overflow-y-auto hidden lg:flex">
      
      {/* --- PROJECT HEADER --- */}
      <div className="p-6 border-b border-gray-800">
        <div className="flex items-center justify-between mb-2">
            <h2 className="text-lg font-bold truncate" title={currentProject.name}>
            {currentProject.name}
            </h2>
            {currentProject.is_public ? (
                <span className="text-xs bg-green-900 text-green-300 px-2 py-1 rounded flex items-center gap-1">
                    <FiGlobe /> Public
                </span>
            ) : (
                <span className="text-xs bg-red-900 text-red-300 px-2 py-1 rounded flex items-center gap-1">
                    <FiLock /> Private
                </span>
            )}
        </div>
        <p className="text-xs text-gray-400 uppercase tracking-wider font-semibold">
          Status: {currentProject.status || 'Active'}
        </p>
      </div>

      {/* --- ABOUT --- */}
      <div className="p-6 border-b border-gray-800">
        <h3 className="text-xs font-bold text-gray-500 uppercase mb-2 flex items-center gap-2">
            <FiInfo /> About Project
        </h3>
        <p className="text-sm text-gray-300 leading-relaxed">
          {currentProject.description || "No description provided."}
        </p>
        {currentProject.long_description && (
             <div className="mt-3 p-3 bg-gray-800 rounded text-xs text-gray-400 italic border border-gray-700">
                {currentProject.long_description}
             </div>
        )}
      </div>

      {/* --- OWNER & TIMELINE --- */}
      <div className="p-6 border-b border-gray-800 space-y-5">
        
        {/* Owner */}
        <div>
            <h3 className="text-xs font-bold text-gray-500 uppercase mb-2">Project Owner</h3>
            <div className="flex items-center gap-3 text-sm text-white bg-gray-800 p-2 rounded-lg border border-gray-700">
                <div className="w-8 h-8 rounded-full bg-blue-600 flex items-center justify-center font-bold shadow-md">
                    {currentProject.owner_name ? currentProject.owner_name.charAt(0).toUpperCase() : <FiUser />}
                </div>
                <span className="font-medium">{currentProject.owner_name || 'System Admin'}</span>
            </div>
        </div>

        {/* Timeline */}
        <div>
             <h3 className="text-xs font-bold text-gray-500 uppercase mb-2">Timeline</h3>
             <div className="space-y-3 text-sm text-gray-300">
                <div className="flex items-center gap-3">
                    <div className="w-8 h-8 rounded-full bg-gray-800 flex items-center justify-center text-blue-400">
                        <FiCalendar size={14} />
                    </div>
                    <div>
                        <p className="text-xs text-gray-500">Created Date</p>
                        <p className="font-medium">{formatDate(currentProject.created_at)}</p>
                    </div>
                </div>
                <div className="flex items-center gap-3">
                    <div className="w-8 h-8 rounded-full bg-gray-800 flex items-center justify-center text-green-400">
                        <FiCheckCircle size={14} />
                    </div>
                    <div>
                        <p className="text-xs text-gray-500">Last Update</p>
                        <p className="font-medium">{formatDate(currentProject.last_updated_at)}</p>
                    </div>
                </div>
             </div>
        </div>
      </div>

      {/* --- MEMBERS --- */}
      <div className="p-6 flex-grow">
        <div className="flex justify-between items-center mb-4">
            <h3 className="text-xs font-bold text-gray-500 uppercase flex items-center gap-2">
                <FiUsers /> Team ({currentMembers?.length || 0})
            </h3>
        </div>

        <div className="space-y-2 max-h-60 overflow-y-auto pr-1 custom-scrollbar">
          {currentMembers && currentMembers.length > 0 ? (
            currentMembers.map((member) => (
              <div key={member.id} className="flex items-center justify-between p-2 hover:bg-gray-800 rounded transition-colors group">
                <div className="flex items-center gap-3">
                  <div className="w-8 h-8 rounded-full bg-gradient-to-tr from-gray-700 to-gray-600 flex items-center justify-center text-xs font-bold text-white border border-gray-500">
                    {member.username.substring(0, 2).toUpperCase()}
                  </div>
                  <div>
                    <p className="text-sm font-medium text-gray-200">{member.username}</p>
                    <p className="text-[10px] text-gray-500 uppercase tracking-wide">{member.role}</p>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="text-center py-4 border border-dashed border-gray-700 rounded text-gray-500 text-sm">
                No members yet
            </div>
          )}
        </div>
      </div>

      {/* --- ADMIN BADGE --- */}
      {user && user.role === 'admin' && (
          <div className="p-3 bg-red-900/30 border-t border-red-500/30 text-center">
              <span className="text-[10px] text-red-300 font-bold tracking-widest uppercase">Admin Privileges Active</span>
          </div>
      )}

    </div>
  );
}
