// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, 
  FiFileText, FiX, FiEye, FiCheckSquare, FiSquare, FiChevronLeft, FiChevronRight, FiExternalLink, FiAirplay, FiAlertCircle 
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  // Localhost kontrolü
  const isLocalhost = typeof window !== 'undefined' && 
    (window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1');

  useEffect(() => {
    if (currentProject) {
      setLoading(true);
      fetchFiles(currentProject.id)
        .then(data => setFiles(Array.isArray(data) ? data : []))
        .finally(() => setLoading(false));
    }
  }, [currentProject]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!previewFile) return;
      if (e.key === 'ArrowRight') handleNextPrev('next');
      if (e.key === 'ArrowLeft') handleNextPrev('prev');
      if (e.key === 'Escape') setPreviewFile(null);
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  });

  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      const results = [];
      for (const file of selectedFiles) {
        const res = await uploadFile(currentProject.id, file);
        if (res && (res.filename || res.id)) {
          results.push(res);
        }
      }
      setFiles(prev => [...results, ...prev]);
    } catch (error) {
      console.error("Upload failed", error);
      alert("Error: Files Couldn't Uploaded.");
    } finally {
      setUploading(false);
      e.target.value = null; 
    }
  };

  const handleDeleteSingle = async (id) => {
    if (window.confirm("Are you sure you want to delete this file?")) {
      try {
        await deleteFile(currentProject.id, id);
        setFiles(files.filter(f => f.id !== id));
        setPreviewFile(null);
      } catch (err) {
        alert("Delete failed.");
      }
    }
  };

  const handleDeleteSelected = async () => {
    if (window.confirm(`Delete ${selectedIds.length} files?`)) {
      setLoading(true);
      try {
        await Promise.all(selectedIds.map(id => deleteFile(currentProject.id, id)));
        setFiles(files.filter(f => !selectedIds.includes(f.id)));
        setSelectedIds([]);
      } catch (err) {
        alert("Error during batch delete.");
      } finally {
        setLoading(false);
      }
    }
  };

  const toggleSelect = (id, e) => {
    e.stopPropagation();
    if (selectedIds.includes(id)) {
      setSelectedIds(selectedIds.filter(itemId => itemId !== id));
    } else {
      setSelectedIds([...selectedIds, id]);
    }
  };

  const toggleSelectAll = () => {
    if (selectedIds.length === files.length) {
      setSelectedIds([]);
    } else {
      setSelectedIds(files.map(f => f.id));
    }
  };

  const handleNextPrev = (direction) => {
    if (!previewFile) return;
    const currentIndex = files.findIndex(f => f.id === previewFile.id);
    let newIndex;
    if (direction === 'next') {
      newIndex = currentIndex + 1 < files.length ? currentIndex + 1 : 0; 
    } else {
      newIndex = currentIndex - 1 >= 0 ? currentIndex - 1 : files.length - 1; 
    }
    setPreviewFile(files[newIndex]);
  };

  const getFileUrl = (filePath) => {
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  const getFileType = (filename) => {
    if (!filename) return 'other';
    const ext = filename.split('.').pop().toLowerCase();
    
    if (['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg'].includes(ext)) return 'image';
    if (ext === 'pdf') return 'pdf';
    if (['doc', 'docx'].includes(ext)) return 'word';
    if (['ppt', 'pptx'].includes(ext)) return 'powerpoint';
    if (['txt', 'md', 'json', 'log', 'js', 'css', 'html'].includes(ext)) return 'text';
    
    return 'other';
  };

  const getPreviewUrl = (file) => {
    const url = getFileUrl(file.file_path);
    const type = getFileType(file.filename);
    
    // PDF, Resim ve Text direkt açılır
    if (type === 'image' || type === 'pdf' || type === 'text') {
      return url;
    }
    
    // Office dosyaları için Viewer (Sadece canlı sunucuda çalışır)
    return `https://view.officeapps.live.com/op/embed.aspx?src=${encodeURIComponent(url)}`;
  };

  // Preview içerik render fonksiyonu
  const renderPreviewContent = () => {
    if (!previewFile) return null;
    const type = getFileType(previewFile.filename);
    const url = getFileUrl(previewFile.file_path);
    const previewUrl = getPreviewUrl(previewFile);

    // Resim
    if (type === 'image') {
      return (
        <img 
          src={url} 
          alt="Preview" 
          className="max-w-full max-h-full object-contain drop-shadow-2xl"
        />
      );
    }

    // Localhost'ta Office Dosyası Uyarısı
    if (isLocalhost && (type === 'word' || type === 'powerpoint')) {
      return (
        <div className="flex flex-col items-center justify-center p-8 text-center max-w-md bg-zinc-900 border border-zinc-800 rounded-2xl">
          <div className="p-4 bg-yellow-500/10 rounded-full mb-4">
            <FiAlertCircle size={48} className="text-yellow-500" />
          </div>
          <h3 className="text-white text-xl font-bold mb-2">Önizleme Kullanılamıyor</h3>
          <p className="text-zinc-400 mb-6">
            Microsoft Office önizleyicisi, güvenlik nedeniyle <strong>Localhost</strong> üzerinde çalışmaz. 
            Dosyayı görüntülemek için lütfen indirin.
          </p>
          <a href={url} download className="px-6 py-3 bg-primary hover:bg-primary-hover text-white rounded-xl font-bold transition-colors flex items-center gap-2 no-underline">
            <FiDownload /> Dosyayı İndir
          </a>
        </div>
      );
    }

    // PDF, Text veya (Canlı Sunucudaysa) Office
    return (
      <iframe 
        src={previewUrl} 
        title="File Preview" 
        className="w-full h-full border-none bg-white rounded-lg shadow-2xl" 
      />
    );
  };

  return (
    <div className="space-y-8 relative w-full">
      {/* HEADER */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-2">
        <div className="flex items-center gap-4">
          <h2 className="text-2xl font-black text-text-main tracking-tight">Project Overview</h2>
          {selectedIds.length > 0 && canEdit && (
            <button 
              onClick={handleDeleteSelected}
              className="flex items-center px-3 py-1.5 bg-red-600 text-white rounded-lg transition-colors text-sm border-none cursor-pointer hover:bg-red-700"
            >
              <FiTrash2 className="mr-2" /> Delete Selected ({selectedIds.length})
            </button>
          )}
        </div>
        
        {canEdit && (
          <div className="relative">
            <input 
              type="file" multiple onChange={handleFileUpload}
              accept=".jpg,.jpeg,.png,.pdf,.doc,.docx,.ppt,.pptx,.txt,.md"
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              disabled={uploading}
            />
            <button className={`relative z-0 flex items-center px-6 py-2.5 rounded-xl font-bold transition-all ${uploading ? 'bg-text-secondary cursor-not-allowed' : 'bg-primary hover:bg-primary-hover'} text-white shadow-lg`}>
              {uploading ? <FiLoader className="animate-spin mr-2" /> : <FiUpload className="mr-2" />}
              {uploading ? 'Uploading...' : 'Upload Files'}
            </button>
          </div>
        )}
      </div>

      {/* README */}
      <div className="bg-surface rounded-2xl border border-border shadow-sm overflow-hidden flex flex-col">
        <div className="bg-app/50 px-5 py-3 border-b border-border flex items-center gap-2">
           <FiFileText className="text-primary" />
           <span className="text-xs font-black uppercase tracking-widest text-text-secondary">Documentation / README</span>
        </div>
        <div className="p-6 max-h-[300px] overflow-y-auto custom-scrollbar bg-surface/50 font-medium whitespace-pre-wrap text-text-main">
          {currentProject?.long_description || "No detailed documentation provided."}
        </div>
      </div>

      {/* FILE LIST */}
      <div className="space-y-4">
        <div className="bg-surface rounded-2xl overflow-hidden shadow-sm border border-border">
          <div className="flex items-center bg-app/80 p-4 border-b border-border text-[10px] font-black text-text-secondary uppercase tracking-widest">
            <div className="w-10 text-center">
              {canEdit && (
                <button onClick={toggleSelectAll} className="hover:text-text-main transition-colors border-none bg-transparent cursor-pointer">
                  {files.length > 0 && selectedIds.length === files.length ? <FiCheckSquare size={16} className="text-primary"/> : <FiSquare size={16}/>}
                </button>
              )}
            </div>
            <div className="flex-1 px-4">Filename</div>
            <div className="w-32 hidden sm:block">Uploaded By</div>
            <div className="w-32 text-right hidden sm:block">Date</div>
            <div className="w-16 text-center">View</div>
          </div>

          <ul className="divide-y divide-border m-0 p-0">
            {files.map((file, idx) => {
              if (!file || !file.filename) return null;
              const type = getFileType(file.filename);
              const isSelected = selectedIds.includes(file.id);

              return (
                <li 
                  key={file.id || idx} 
                  onClick={() => setPreviewFile(file)}
                  className={`flex items-center p-4 hover:bg-app/50 transition-all cursor-pointer group ${isSelected ? 'bg-primary/5' : ''}`}
                >
                  <div className="w-10 flex justify-center" onClick={(e) => e.stopPropagation()}>
                    {canEdit && (
                      <button onClick={(e) => toggleSelect(file.id, e)} className={`transition-colors border-none bg-transparent cursor-pointer ${isSelected ? 'text-primary' : 'text-text-secondary hover:text-text-main'}`}>
                        {isSelected ? <FiCheckSquare size={18}/> : <FiSquare size={18}/>}
                      </button>
                    )}
                  </div>

                  <div className="flex-1 px-4 flex items-center min-w-0">
                    <div className={`p-2.5 rounded-xl mr-4 shadow-sm ${
                      type === 'word' ? 'bg-blue-500/10 text-blue-600' : 
                      type === 'powerpoint' ? 'bg-orange-500/10 text-orange-600' : 
                      type === 'text' ? 'bg-emerald-500/10 text-emerald-600' : 
                      type === 'image' ? 'bg-purple-500/10 text-purple-600' : 'bg-app text-text-secondary'
                    }`}>
                      {type === 'word' ? <FiFileText size={18} /> : type === 'powerpoint' ? <FiAirplay size={18} /> : <FiFile size={18} />}
                    </div>
                    <span className="text-text-main font-bold truncate text-sm">{file.filename}</span>
                  </div>

                  <div className="w-32 text-xs font-bold text-text-secondary hidden sm:block truncate uppercase tracking-tighter">{file.uploader_name}</div>
                  <div className="w-32 text-right text-xs font-bold text-text-secondary hidden sm:block font-mono opacity-60">{new Date(file.created_at).toLocaleDateString()}</div>
                  <div className="w-16 flex justify-center"><FiEye className="text-text-secondary group-hover:text-primary transition-colors" size={18} /></div>
                </li>
              );
            })}
          </ul>
          {files.length === 0 && !loading && (
             <div className="p-8 text-center text-text-secondary italic">No files uploaded yet.</div>
          )}
        </div>
      </div>

      {/* --- FIXED FULL SCREEN PREVIEW MODAL --- */}
      {previewFile && (
        <div className="fixed inset-0 z-[99999] bg-black/95 flex flex-col overflow-hidden animate-fade-in">
          
          {/* Top Control Bar */}
          <div className="flex-none w-full flex items-center justify-between px-6 py-4 bg-zinc-900/80 backdrop-blur-md border-b border-white/10 z-[100]">
              <div className="flex items-center gap-4 text-white overflow-hidden">
                  <button onClick={() => setPreviewFile(null)} className="flex-none p-2 hover:bg-white/10 rounded-full transition-all border-none bg-transparent cursor-pointer text-white">
                      <FiX size={24} />
                  </button>
                  <span className="font-bold truncate text-sm md:text-base">{previewFile.filename}</span>
              </div>
              
              <div className="flex items-center gap-2 md:gap-3 flex-none">
                  <a href={getFileUrl(previewFile.file_path)} target="_blank" rel="noopener noreferrer" className="hidden md:flex px-4 py-2 bg-white/10 hover:bg-white/20 text-white rounded-lg text-xs font-bold transition-all items-center gap-2 no-underline shadow-lg border-none cursor-pointer">
                      <FiExternalLink size={16} /> Open
                  </a>
                  <a href={getFileUrl(previewFile.file_path)} download className="px-4 py-2 bg-primary hover:bg-primary-hover text-white rounded-lg text-xs font-bold transition-all flex items-center gap-2 no-underline shadow-lg border-none cursor-pointer">
                      <FiDownload size={16} /> <span className="hidden md:inline">Download</span>
                  </a>
                  {canEdit && (
                      <button onClick={() => handleDeleteSingle(previewFile.id)} className="p-2 bg-red-600/20 hover:bg-red-600 text-red-500 hover:text-white rounded-lg transition-all border-none cursor-pointer">
                          <FiTrash2 size={18} />
                      </button>
                  )}
              </div>
          </div>

          {/* Main Content Area */}
          <div className="flex-1 relative w-full h-full flex items-center justify-center p-4 md:p-8 overflow-hidden">
            
            {/* Nav Arrows */}
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('prev'); }} className="absolute left-2 md:left-6 z-[120] text-white/30 hover:text-white p-3 hover:bg-white/10 rounded-full transition-all border-none cursor-pointer">
                <FiChevronLeft size={40} />
            </button>
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('next'); }} className="absolute right-2 md:right-6 z-[120] text-white/30 hover:text-white p-3 hover:bg-white/10 rounded-full transition-all border-none cursor-pointer">
                <FiChevronRight size={40} />
            </button>

            {/* Content Injection Wrapper - Ensures fitting */}
            <div className="w-full h-full flex items-center justify-center relative z-[110]">
               {renderPreviewContent()}
            </div>
            
            {/* Pagination Info */}
            <div className="absolute bottom-6 left-1/2 -translate-x-1/2 z-[120] pointer-events-none">
                <span className="bg-zinc-800/80 text-white/80 px-4 py-1.5 rounded-full text-[10px] font-black tracking-widest uppercase backdrop-blur-md border border-white/5 shadow-xl">
                    {files.findIndex(f => f.id === previewFile.id) + 1} / {files.length}
                </span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
