// src/pages/Users.js
import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { 
  FiUserPlus, FiMoreVertical, FiEdit2, FiTrash2, FiChevronLeft, FiChevronRight, FiX, FiSearch, FiUsers, FiLoader 
} from 'react-icons/fi';

function Users() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState(""); 
  const [openMenuId, setOpenMenuId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [formData, setFormData] = useState({ username: '', email: '', password: '', role: 'student' });
  const itemsPerPage = 7;

  useEffect(() => { fetchUsers(); }, []);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await api.get('/api/auth/users');
      setUsers(response.data.sort((a, b) => new Date(b.created_at) - new Date(a.created_at)));
    } catch (err) { console.error(err); } finally { setLoading(false); }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    try {
      if (selectedItem) await api.put(`/api/auth/users/${selectedItem.id}`, formData);
      else await api.post('/api/auth/register', formData);
      fetchUsers();
      setShowModal(false);
    } catch (err) { alert("Operation failed."); } finally { setIsSubmitting(false); }
  };

  const handleDeleteUser = async (user) => {
    if (!window.confirm(`Delete ${user.username}?`)) return;
    try {
      await api.delete(`/api/auth/users/${user.id}`);
      fetchUsers();
    } catch (err) { alert("Delete failed."); }
  };

  const filteredData = users.filter(u => u.username?.toLowerCase().includes(searchTerm.toLowerCase()) || u.email?.toLowerCase().includes(searchTerm.toLowerCase()));
  const currentItems = filteredData.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);
  const totalPages = Math.max(Math.ceil(filteredData.length / itemsPerPage), 1);

  return (
    <div className="flex flex-col h-[calc(100vh-140px)] container mx-auto max-w-6xl px-4 py-4 overflow-hidden">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-black text-text-main tracking-tight flex items-center">
          <FiUsers className="mr-3 text-primary" /> User Management
        </h1>
        <button onClick={() => { setSelectedItem(null); setFormData({ username: '', email: '', password: '', role: 'student' }); setShowModal(true); }} className="px-4 py-2 bg-primary text-white rounded-lg shadow-lg font-bold text-sm">
          <FiUserPlus className="mr-2" /> Add User
        </button>
      </div>

      <div className="relative mb-4 w-full md:w-80">
        <FiSearch className="absolute left-3 top-2.5 text-text-secondary" />
        <input 
          type="text" placeholder="Search users..." value={searchTerm}
          onChange={(e) => { setSearchTerm(e.target.value); setCurrentPage(1); }}
          className="w-full bg-surface border border-border text-text-main text-sm rounded-lg pl-10 py-2 outline-none"
        />
      </div>

      <div className="flex-grow bg-surface rounded-xl border border-border shadow-xl overflow-hidden flex flex-col">
        <div className="overflow-x-auto flex-1">
          {loading ? <div className="flex justify-center p-20"><FiLoader className="animate-spin text-primary" size={40} /></div> : (
            <table className="w-full text-left">
              <thead className="bg-app/80 text-[11px] uppercase text-text-secondary font-black border-b border-border">
                <tr><th className="px-6 py-4">User Details</th><th className="px-6 py-4">Role</th><th className="px-6 py-4 text-right">Joined</th><th className="px-6 py-4 w-10"></th></tr>
              </thead>
              <tbody className="divide-y divide-border text-sm">
                {currentItems.map((u) => (
                  <tr key={u.id} className="hover:bg-surface-hover transition-colors">
                    <td className="px-6 py-3 flex items-center">
                      <div className="w-9 h-9 rounded-full bg-primary/10 text-primary flex items-center justify-center font-black mr-3">{u.username?.charAt(0).toUpperCase()}</div>
                      <div className="flex flex-col"><span className="font-bold text-text-main">{u.username}</span><span className="text-[11px] opacity-60">{u.email}</span></div>
                    </td>
                    <td className="px-6 py-3"><span className={`text-[10px] font-black px-2 py-0.5 rounded border ${u.role === 'admin' ? 'text-red-500' : 'text-blue-500'}`}>{u.role?.toUpperCase()}</span></td>
                    <td className="px-6 py-3 text-right opacity-40 font-bold">{new Date(u.created_at).toLocaleDateString()}</td>
                    <td className="px-6 py-3 relative">
                      <button onClick={() => setOpenMenuId(openMenuId === u.id ? null : u.id)} className="p-2 text-text-secondary"><FiMoreVertical size={16} /></button>
                      {openMenuId === u.id && (
                        <div className="absolute right-8 top-2 w-32 bg-surface border border-border rounded-xl shadow-2xl z-50 py-1">
                          <button onClick={() => { setSelectedItem(u); setFormData({username:u.username, email:u.email, role:u.role, password:''}); setShowModal(true); setOpenMenuId(null); }} className="w-full text-left px-4 py-2 text-xs font-bold text-text-main flex items-center"><FiEdit2 className="mr-2"/> EDIT</button>
                          <button onClick={() => handleDeleteUser(u)} className="w-full text-left px-4 py-2 text-xs font-bold text-red-500 flex items-center"><FiTrash2 className="mr-2"/> DELETE</button>
                        </div>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>

      {showModal && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center bg-black/80 backdrop-blur-sm">
          <div className="bg-surface border border-border rounded-2xl w-full max-w-md p-8 relative">
            <button onClick={() => setShowModal(false)} className="absolute top-6 right-6 text-text-secondary"><FiX size={20}/></button>
            <h2 className="text-xl font-black mb-6">{selectedItem ? 'Update User' : 'New User'}</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input value={formData.username} onChange={(e)=>setFormData({...formData, username:e.target.value})} className="w-full py-3 px-4 bg-app border border-border rounded-xl text-text-main" placeholder="Username"/>
              <input value={formData.email} onChange={(e)=>setFormData({...formData, email:e.target.value})} className="w-full py-3 px-4 bg-app border border-border rounded-xl text-text-main" placeholder="Email"/>
              <input type="password" value={formData.password} onChange={(e)=>setFormData({...formData, password:e.target.value})} className="w-full py-3 px-4 bg-app border border-border rounded-xl text-text-main" placeholder="Password"/>
              <select value={formData.role} onChange={(e)=>setFormData({...formData, role:e.target.value})} className="w-full py-3 px-4 bg-app border border-border rounded-xl text-text-main">
                <option value="student">Student</option>
                <option value="admin">Admin</option>
              </select>
              <button type="submit" disabled={isSubmitting} className="w-full bg-primary text-white font-black rounded-xl py-3 mt-4">{isSubmitting ? 'Saving...' : 'Save'}</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Users;
