// src/pages/SettingsPage.js

import React, { useState } from 'react';
import { FiUser, FiLock, FiMonitor, FiSave } from 'react-icons/fi';

export default function SettingsPage() {
  const [activeTab, setActiveTab] = useState('profile');

  // Tab İçerikleri (Notifications kaldırıldı)
  const renderContent = () => {
    switch (activeTab) {
      case 'profile':
        return (
          <div className="space-y-6 animate-fade-in">
            <h3 className="text-lg font-medium text-white border-b border-gray-700 pb-2">Profile Details</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                    <label className="block text-sm text-gray-400 mb-1">Full Name</label>
                    <input type="text" className="w-full bg-gray-900 border border-gray-700 rounded-lg p-2.5 text-white focus:border-blue-500 outline-none" placeholder="Your Name" />
                </div>
                <div>
                    <label className="block text-sm text-gray-400 mb-1">Email</label>
                    <input type="email" className="w-full bg-gray-900 border border-gray-700 rounded-lg p-2.5 text-white focus:border-blue-500 outline-none" placeholder="email@example.com" disabled />
                </div>
                <div className="md:col-span-2">
                    <label className="block text-sm text-gray-400 mb-1">Bio</label>
                    <textarea className="w-full bg-gray-900 border border-gray-700 rounded-lg p-2.5 text-white focus:border-blue-500 outline-none h-24" placeholder="Tell us about yourself..." />
                </div>
            </div>
          </div>
        );
      case 'security':
        return (
          <div className="space-y-6 animate-fade-in">
            <h3 className="text-lg font-medium text-white border-b border-gray-700 pb-2">Security & Password</h3>
            <div className="space-y-4 max-w-md">
                <div>
                    <label className="block text-sm text-gray-400 mb-1">Current Password</label>
                    <input type="password" className="w-full bg-gray-900 border border-gray-700 rounded-lg p-2.5 text-white focus:border-blue-500 outline-none" />
                </div>
                <div>
                    <label className="block text-sm text-gray-400 mb-1">New Password</label>
                    <input type="password" className="w-full bg-gray-900 border border-gray-700 rounded-lg p-2.5 text-white focus:border-blue-500 outline-none" />
                </div>
                <div>
                    <label className="block text-sm text-gray-400 mb-1">Confirm New Password</label>
                    <input type="password" className="w-full bg-gray-900 border border-gray-700 rounded-lg p-2.5 text-white focus:border-blue-500 outline-none" />
                </div>
            </div>
          </div>
        );
      case 'appearance':
        return (
          <div className="space-y-6 animate-fade-in">
            <h3 className="text-lg font-medium text-white border-b border-gray-700 pb-2">Appearance Settings</h3>
            <div className="space-y-4">
                <p className="text-gray-400 text-sm">Theme preferences will be available soon.</p>
                {/* Gelecekte tema ayarları buraya eklenebilir */}
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 max-w-4xl h-[calc(100vh-100px)]">
      <h1 className="text-3xl font-bold text-white mb-6">Settings</h1>

      {/* --- ÜST TARAFA ALINAN TAB MENÜSÜ --- */}
      <div className="flex space-x-2 border-b border-gray-700 mb-8 overflow-x-auto">
        <button 
            onClick={() => setActiveTab('profile')} 
            className={`flex items-center px-6 py-3 text-sm font-medium transition-colors border-b-2 ${
                activeTab === 'profile' 
                ? 'border-blue-500 text-blue-400' 
                : 'border-transparent text-gray-400 hover:text-white hover:border-gray-600'
            }`}
        >
            <FiUser className="mr-2" /> Profile
        </button>
        
        <button 
            onClick={() => setActiveTab('security')} 
            className={`flex items-center px-6 py-3 text-sm font-medium transition-colors border-b-2 ${
                activeTab === 'security' 
                ? 'border-blue-500 text-blue-400' 
                : 'border-transparent text-gray-400 hover:text-white hover:border-gray-600'
            }`}
        >
            <FiLock className="mr-2" /> Security
        </button>

        <button 
            onClick={() => setActiveTab('appearance')} 
            className={`flex items-center px-6 py-3 text-sm font-medium transition-colors border-b-2 ${
                activeTab === 'appearance' 
                ? 'border-blue-500 text-blue-400' 
                : 'border-transparent text-gray-400 hover:text-white hover:border-gray-600'
            }`}
        >
            <FiMonitor className="mr-2" /> Appearance
        </button>
      </div>

      {/* --- İÇERİK ALANI --- */}
      <div className="bg-gray-800 rounded-xl border border-gray-700 p-8 shadow-xl min-h-[400px]">
          {renderContent()}
          
          {/* Kaydet Butonu (Her tabda ortak) */}
          <div className="mt-10 pt-6 border-t border-gray-700 flex justify-end">
              <button className="flex items-center px-6 py-2.5 bg-blue-600 hover:bg-blue-500 text-white rounded-lg font-medium transition-colors shadow-lg">
                  <FiSave className="mr-2" /> Save Changes
              </button>
          </div>
      </div>
    </div>
  );
}
