// src/pages/Users.js

import React, { useState, useEffect, useRef } from 'react';
import api from '../services/api';
import { 
  FiMail, FiUserPlus, FiMoreVertical, FiEdit2, FiTrash2, 
  FiEye, FiChevronLeft, FiChevronRight, FiX, FiUser, FiCheck, FiSearch, FiLock, FiShield
} from 'react-icons/fi';

function Users() {
  // --- STATE ---
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage] = useState(7); // Listeyi sıkılaştırdık, bir sayfaya 7 kişi sığabilir
  const [searchTerm, setSearchTerm] = useState(""); 
  const [openMenuUserId, setOpenMenuUserId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  // EDIT STATE: Hangi kullanıcının düzenlendiğini tutar (null ise Ekleme modudur)
  const [selectedUser, setSelectedUser] = useState(null);

  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    role: 'student'
  });

  const menuRef = useRef(null);

  // --- 1. VERİ ÇEKME ---
  useEffect(() => {
    fetchUsers();
  }, []);

  useEffect(() => {
    function handleClickOutside(event) {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setOpenMenuUserId(null);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => { document.removeEventListener("mousedown", handleClickOutside); };
  }, [menuRef]);

  const fetchUsers = async () => {
    try {
      const response = await api.get('/api/auth/users');
      if (Array.isArray(response.data)) {
         const sortedUsers = response.data.sort((a, b) => new Date(b.created_at || Date.now()) - new Date(a.created_at || Date.now()));
         setUsers(sortedUsers);
      } else {
         setUsers([]);
      }
    } catch (err) {
      console.error("Kullanıcılar çekilemedi:", err);
      setUsers([]);
    } finally {
      setLoading(false);
    }
  };

  // --- 2. FİLTRELEME & SAYFALAMA ---
  const filteredUsers = users.filter(user => 
    (user.username && user.username.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (user.email && user.email.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);
  
  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  const handleSearch = (e) => { setSearchTerm(e.target.value); setCurrentPage(1); };

  // --- 3. MODAL İŞLEMLERİ (EKLEME & DÜZENLEME) ---
  
  const handleInputChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // "Add User" butonuna basınca
  const openAddModal = () => {
      setSelectedUser(null); // Edit modu değil
      setFormData({ username: '', email: '', password: '', role: 'student' });
      setShowModal(true);
  };

  // Dropdown içindeki "Edit" butonuna basınca
  const openEditModal = (user) => {
      setSelectedUser(user); // Edit modu aktif
      setFormData({
          username: user.username,
          email: user.email,
          password: '', // Şifre boş gelir, isterse değiştirir
          role: user.role || 'student'
      });
      setOpenMenuUserId(null); // Menüyü kapat
      setShowModal(true); // Modalı aç
  };

  // Form Submit (Hem Ekleme Hem Güncelleme)
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Validasyon
    if (!formData.username || !formData.email) {
        alert("Username and Email are required.");
        setIsSubmitting(false);
        return;
    }
    // Yeni eklerken şifre şart, düzenlerken opsiyonel
    if (!selectedUser && !formData.password) {
        alert("Password is required for new users.");
        setIsSubmitting(false);
        return;
    }

    try {
        if (selectedUser) {
            // --- GÜNCELLEME (EDIT) ---
            // Not: Backend'de update rotan '/api/users/:id' gibi olmalı.
            // Eğer yoksa backend'e eklemen gerekir. Şimdilik simüle ediyoruz veya var sayıyoruz.
            const payload = { ...formData };
            if (!payload.password) delete payload.password; // Şifre boşsa gönderme

            // BURASI ÖNEMLİ: Backend güncelleme rotanıza göre düzenleyin
            // Örn: await api.put(`/api/users/${selectedUser.id}`, payload);
            
            // Simülasyon (Backend güncelleme kodu olmadığı için sadece frontend'de güncelliyoruz)
            const updatedUsers = users.map(u => u.id === selectedUser.id ? { ...u, ...payload } : u);
            setUsers(updatedUsers);
            alert(`User updated: ${payload.username}`);

        } else {
            // --- YENİ EKLEME (ADD) ---
            const payload = {
                username: formData.username,
                email: formData.email,
                password: formData.password
            };
            const response = await api.post('/api/auth/register', payload);
            const newUser = response.data.user || response.data; 
            const userWithDate = { ...newUser, created_at: new Date().toISOString() };
            setUsers([userWithDate, ...users]);
            alert(`User created: ${newUser.username}`);
        }

        setShowModal(false);
        setFormData({ username: '', email: '', password: '', role: 'student' });

    } catch (err) {
        console.error(err);
        alert(err.response?.data?.message || "Operation failed.");
    } finally {
        setIsSubmitting(false);
    }
  };

  const toggleMenu = (id, e) => { 
      e.stopPropagation(); 
      setOpenMenuUserId(openMenuUserId === id ? null : id); 
  };

  if (loading) return <div className="p-10 text-center text-gray-400">Loading...</div>;

  return (
    <div className="flex flex-col h-[calc(100vh-140px)] container mx-auto max-w-6xl px-4 py-4">
      
      {/* HEADER */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-4 gap-4">
        <div>
            <h1 className="text-2xl font-bold text-white">User Management</h1>
            <p className="text-xs text-gray-400 mt-1">Total <span className="text-white">{filteredUsers.length}</span> records.</p>
        </div>
        <div className="flex items-center gap-3 w-full md:w-auto">
            <div className="relative flex-1 md:w-64">
                <FiSearch className="absolute left-3 top-2.5 text-gray-500" />
                <input 
                    type="text" 
                    placeholder="Search users..." 
                    value={searchTerm} 
                    onChange={handleSearch} 
                    className="w-full bg-gray-800 border border-gray-700 text-gray-300 text-sm rounded-lg pl-10 pr-4 py-2 focus:border-blue-500 outline-none" 
                />
            </div>
            <button onClick={openAddModal} className="flex items-center px-4 py-2 bg-blue-600 hover:bg-blue-500 text-white rounded-lg shadow-lg font-medium text-sm whitespace-nowrap">
                <FiUserPlus className="mr-2" size={16} /> Add User
            </button>
        </div>
      </div>

      {/* TABLE CONTAINER */}
      <div className="flex-1 bg-gray-800 rounded-xl border border-gray-700 shadow-xl overflow-hidden flex flex-col relative" ref={menuRef}>
        <div className="overflow-x-auto flex-1 custom-scrollbar">
          <table className="w-full text-left text-gray-300">
            <thead className="bg-gray-900/50 text-xs uppercase text-gray-400 font-semibold sticky top-0 z-10 backdrop-blur-sm">
              <tr>
                <th className="px-6 py-3">User</th>
                <th className="px-6 py-3">Email</th>
                <th className="px-6 py-3">Role</th>
                <th className="px-6 py-3 text-right">Date</th>
                <th className="px-6 py-3 w-10"></th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-700/50 text-sm">
              {currentUsers.length > 0 ? (
                  currentUsers.map((user) => (
                    <tr key={user.id} className="hover:bg-gray-700/30 transition-colors">
                      <td className="px-6 py-2.5">
                        <div className="flex items-center">
                          <div className="w-8 h-8 rounded-full bg-gradient-to-br from-indigo-500 to-purple-600 flex items-center justify-center text-white font-bold mr-3 text-xs">
                            {user.username?.charAt(0).toUpperCase()}
                          </div>
                          <span className="font-medium text-white">{user.username}</span>
                        </div>
                      </td>
                      <td className="px-6 py-2.5 text-gray-400 flex items-center">
                        <FiMail className="mr-2 opacity-50" /> {user.email}
                      </td>
                      <td className="px-6 py-2.5">
                        <span className={`text-[10px] font-bold px-2 py-0.5 rounded-full border ${user.role === 'admin' ? 'border-red-500/30 text-red-400 bg-red-500/10' : 'border-blue-500/30 text-blue-400 bg-blue-500/10'}`}>
                            {user.role ? user.role.toUpperCase() : 'STUDENT'}
                        </span>
                      </td>
                      <td className="px-6 py-2.5 text-right text-xs text-gray-500 font-mono">
                        {new Date(user.created_at || Date.now()).toLocaleDateString('en-US')}
                      </td>
                      <td className="px-6 py-2.5 relative">
                        <button onClick={(e) => toggleMenu(user.id, e)} className="p-1.5 rounded-full hover:bg-gray-600 text-gray-500">
                            <FiMoreVertical size={16} />
                        </button>
                        {openMenuUserId === user.id && (
                            <div className="absolute right-8 top-2 w-32 bg-gray-800 border border-gray-600 rounded-lg shadow-2xl z-50 animate-fade-in">
                                <button 
                                    onClick={() => openEditModal(user)}
                                    className="w-full text-left px-4 py-2 text-xs text-gray-300 hover:bg-gray-700 flex items-center"
                                >
                                    <FiEdit2 className="mr-2"/> Edit
                                </button>
                                <button className="w-full text-left px-4 py-2 text-xs text-red-400 hover:bg-red-500/10 flex items-center"><FiTrash2 className="mr-2"/> Delete</button>
                            </div>
                        )}
                      </td>
                    </tr>
                  ))
              ) : (
                  <tr><td colSpan="5" className="p-10 text-center text-gray-500">No users found.</td></tr>
              )}
            </tbody>
          </table>
        </div>
        
        {/* PAGINATION */}
        <div className="bg-gray-900/50 border-t border-gray-700 p-3 flex items-center justify-between">
            <span className="text-xs text-gray-500">Page {currentPage} of {totalPages || 1}</span>
            <div className="flex space-x-1">
                <button onClick={() => paginate(currentPage - 1)} disabled={currentPage === 1} className="p-1.5 rounded bg-gray-700 hover:bg-gray-600 disabled:opacity-50"><FiChevronLeft size={16}/></button>
                <button onClick={() => paginate(currentPage + 1)} disabled={currentPage === totalPages || totalPages === 0} className="p-1.5 rounded bg-gray-700 hover:bg-gray-600 disabled:opacity-50"><FiChevronRight size={16}/></button>
            </div>
        </div>
      </div>

      {/* --- MODAL (ADD & EDIT) --- */}
      {showModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/70 backdrop-blur-sm animate-fade-in">
            <div className="bg-gray-800 border border-gray-700 rounded-2xl w-full max-w-md shadow-2xl p-6 relative">
                <button onClick={() => setShowModal(false)} className="absolute top-4 right-4 text-gray-400 hover:text-white"><FiX size={24} /></button>
                
                <h2 className="text-2xl font-bold text-white mb-2 flex items-center">
                    {selectedUser ? (
                        <><FiEdit2 className="mr-3 text-yellow-500" /> Edit User</>
                    ) : (
                        <><FiUserPlus className="mr-3 text-blue-500" /> New User</>
                    )}
                </h2>
                <p className="text-sm text-gray-400 mb-6">
                    {selectedUser ? `Updating details for ${selectedUser.username}` : "Create a new student account."}
                </p>

                <form onSubmit={handleSubmit} className="space-y-4">
                    {/* Username */}
                    <div className="relative">
                        <div className="absolute top-0 left-0 flex items-center h-full pl-3 pointer-events-none">
                            <FiUser className="w-5 h-5 text-gray-500" />
                        </div>
                        <input 
                            type="text" name="username" required 
                            value={formData.username} onChange={handleInputChange} 
                            className="w-full py-2.5 pl-10 pr-4 text-gray-200 bg-gray-900 border border-gray-700 rounded-lg placeholder:text-gray-500 focus:outline-none focus:border-blue-500" 
                            placeholder="Username"
                        />
                    </div>

                    {/* Email */}
                    <div className="relative">
                        <div className="absolute top-0 left-0 flex items-center h-full pl-3 pointer-events-none">
                            <FiMail className="w-5 h-5 text-gray-500" />
                        </div>
                        <input 
                            type="email" name="email" required 
                            value={formData.email} onChange={handleInputChange} 
                            className="w-full py-2.5 pl-10 pr-4 text-gray-200 bg-gray-900 border border-gray-700 rounded-lg placeholder:text-gray-500 focus:outline-none focus:border-blue-500" 
                            placeholder="Email"
                        />
                    </div>

                    {/* Password (Edit modunda opsiyonel) */}
                    <div className="relative">
                        <div className="absolute top-0 left-0 flex items-center h-full pl-3 pointer-events-none">
                            <FiLock className="w-5 h-5 text-gray-500" />
                        </div>
                        <input 
                            type="password" name="password" 
                            required={!selectedUser} // Sadece yeni eklerken zorunlu
                            value={formData.password} onChange={handleInputChange} 
                            className="w-full py-2.5 pl-10 pr-4 text-gray-200 bg-gray-900 border border-gray-700 rounded-lg placeholder:text-gray-500 focus:outline-none focus:border-blue-500" 
                            placeholder={selectedUser ? "New Password (Optional)" : "Password"}
                        />
                    </div>

                    {/* Role Selection (SADECE EDIT MODUNDA GÖZÜKÜR) */}
                    {selectedUser && (
                        <div className="relative">
                            <div className="absolute top-0 left-0 flex items-center h-full pl-3 pointer-events-none">
                                <FiShield className="w-5 h-5 text-gray-500" />
                            </div>
                            <select 
                                name="role" 
                                value={formData.role} 
                                onChange={handleInputChange}
                                className="w-full py-2.5 pl-10 pr-4 text-gray-200 bg-gray-900 border border-gray-700 rounded-lg focus:outline-none focus:border-blue-500 appearance-none"
                            >
                                <option value="student">Student</option>
                                <option value="admin">Admin</option>
                            </select>
                        </div>
                    )}
                    
                    <button type="submit" disabled={isSubmitting} className={`w-full text-white font-medium rounded-lg text-sm px-5 py-3 mt-4 flex justify-center items-center transition-colors ${selectedUser ? 'bg-yellow-600 hover:bg-yellow-500' : 'bg-blue-600 hover:bg-blue-500'}`}>
                        {isSubmitting ? 'Saving...' : (
                            <>{selectedUser ? <><FiCheck className="mr-2"/> Update User</> : <><FiCheck className="mr-2"/> Create User</>}</>
                        )}
                    </button>
                </form>
            </div>
        </div>
      )}
    </div>
  );
}

export default Users;
