// src/components/projects/ProjectDiscussion.js

import React, { useState } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import CommentForm from './CommentForm'; 
import CommentList from './CommentList'; // Güncellenmiş CommentList'i kullanır

export default function ProjectDiscussion() {
  const { currentProject, addComment, loading } = useProjectContext();
  const [refreshKey, setRefreshKey] = useState(0);

  const handleDiscussionSubmit = async (text) => {
    if (!currentProject) return;
    try {
      await addComment(currentProject.id, text);
      setRefreshKey(prevKey => prevKey + 1);
    } catch (error) {
      console.error("Failed to add discussion comment:", error);
    }
  };

  if (!currentProject) {
    return null;
  }

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4 text-white">Discussions</h2>
      
      {/* Form (public_viewer hariç herkese görünür) */}
      (
         <CommentForm 
           onSubmit={handleDiscussionSubmit}
           loading={loading}
         />
      )
      
      {/* CommentList artık 'projectId' ve 'refreshKey' prop'larını alır */}
      <CommentList 
        projectId={currentProject.id} 
        refreshKey={refreshKey} 
      />
    </div>
  );
}
