// src/components/layout/Header.js

import api from '../../services/api';
import React, { useState, useEffect, useRef } from 'react';
import { FiBell, FiChevronDown } from 'react-icons/fi'; 
import { Link } from 'react-router-dom';
import ProfileDropdown from './ProfileDropdown';
import { useAuth } from '../../context/AuthContext';
import LogoImage from '../../assets/logo.png';

function Header({ onToggleRightSidebar }) {
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const { user } = useAuth();

  // Profil Dropdown için dışarı tıklama kontrolü
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [dropdownRef]);

  // --- BİLDİRİM STATE'LERİ ---
  const [notifications, setNotifications] = useState([]);
  const [showNotifications, setShowNotifications] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const notifRef = useRef(null);

  // Bildirimleri çek
  const fetchNotifications = async () => {
    try {
      const res = await api.get('/api/notifications');
      
      // CRASH PROTECTION: Check if result is actually an array
      if (Array.isArray(res.data)) {
          setNotifications(res.data);
          setUnreadCount(res.data.filter(n => !n.is_read).length);
      } else {
          console.warn("Notifications API returned non-array data:", res.data);
          setNotifications([]); // Set empty list to prevent crash
          setUnreadCount(0);
      }
    } catch (err) {
      console.error("Notification Error:", err);
      // Do not throw error, just ignore it so the page doesn't crash
      setNotifications([]);
    }
  };

  // Sayfa açılınca ve her 30 saniyede bir kontrol et
  useEffect(() => {
    fetchNotifications();
    const interval = setInterval(fetchNotifications, 30000);
    
    // Bildirim menüsü için dışarı tıklama kontrolü
    function handleClickOutsideNotif(event) {
      if (notifRef.current && !notifRef.current.contains(event.target)) {
        setShowNotifications(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutsideNotif);
    
    return () => {
      clearInterval(interval);
      document.removeEventListener("mousedown", handleClickOutsideNotif);
    };
  }, []);

  // Tıklayınca okundu işaretle
  const handleNotificationClick = async (notif) => {
    if (!notif.is_read) {
        try {
            await api.put(`/api/notifications/${notif.id}/read`);
            setNotifications(prev => prev.map(n => n.id === notif.id ? { ...n, is_read: true } : n));
            setUnreadCount(prev => Math.max(0, prev - 1));
        } catch (error) {
            console.error("Okundu işaretlenemedi", error);
        }
    }
  };

  return (
    <header className="bg-black bg-opacity-40 backdrop-blur-md text-white shadow-sm border-b border-white/10 z-30 relative">
      <div className="px-6 py-3 flex justify-between items-center">
        
        {/* Logo ve Marka Alanı */}
        <Link to="/dashboard" className="flex items-center hover:text-gray-300 transition-colors group">
          <img 
            src={LogoImage} 
            alt="Logo" 
            className="h-10 w-10 object-contain mr-3" 
          />
          <div className="flex items-baseline">
            <span className="text-xl font-bold tracking-wide">GazozHub</span>
          </div>
        </Link>

        <div className="flex items-center space-x-4">

          {/* --- BİLDİRİM ALANI (DÜZELTİLEN KISIM) --- */}
          <div className="relative" ref={notifRef}>
            <button 
                onClick={() => setShowNotifications(!showNotifications)}
                className="p-2 rounded-full hover:bg-white/10 transition relative"
            >
                <FiBell size={20} />
                {/* Kırmızı Bildirim Noktası */}
                {unreadCount > 0 && (
                    <span className="absolute top-1 right-1 w-2.5 h-2.5 bg-red-500 rounded-full border border-gray-900"></span>
                )}
            </button>

            {/* Bildirim Dropdown Menüsü */}
            {showNotifications && (
                <div className="absolute right-0 mt-3 w-80 bg-gray-900 border border-gray-700 rounded-lg shadow-xl py-2 z-50 text-left">
                    <div className="px-4 py-2 border-b border-gray-800 flex justify-between items-center">
                        <h3 className="font-semibold text-white">Notifications</h3>
                        <span className="text-xs text-gray-400">{unreadCount} unread</span>
                    </div>
                    
                    <div className="max-h-64 overflow-y-auto">
                        {notifications.length === 0 ? (
                            <div className="p-4 text-center text-gray-500 text-sm">No notifications yet.</div>
                        ) : (
                            notifications.map((notif) => (
                                <div 
                                    key={notif.id} 
                                    onClick={() => handleNotificationClick(notif)}
                                    className={`px-4 py-3 border-b border-gray-800 last:border-0 hover:bg-gray-800 cursor-pointer transition-colors ${!notif.is_read ? 'bg-gray-800/50 border-l-2 border-blue-500' : ''}`}
                                >
                                    <p className={`text-sm ${!notif.is_read ? 'text-white font-medium' : 'text-gray-400'}`}>
                                        {notif.message}
                                    </p>
                                    <p className="text-xs text-gray-500 mt-1">
                                        {new Date(notif.created_at).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                                    </p>
                                </div>
                            ))
                        )}
                    </div>
                </div>
            )}
          </div>
          {/* --- BİLDİRİM ALANI BİTİŞ --- */}
          
          {/* Profil Dropdown */}
          <div className="relative" ref={dropdownRef}>
            <button 
              onClick={() => setDropdownOpen(!isDropdownOpen)} 
              className="flex items-center space-x-2 p-2 rounded-full hover:bg-white/10 transition"
            >
              <span className="font-medium">{user ? user.username : 'Guest'}</span>
              <FiChevronDown size={16} />
            </button>
            
            <ProfileDropdown isOpen={isDropdownOpen} />
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header;
