import React, { createContext, useState, useContext, useCallback } from 'react';
// DÜZELTME 1: Düz 'axios' yerine kendi 'api' servisimizi kullanıyoruz.
import api from '../services/api'; 
import { useAuth } from './AuthContext';

export const ProjectContext = createContext();
export const useProjectContext = () => useContext(ProjectContext);

export const ProjectProvider = ({ children }) => {
  const [projects, setProjects] = useState([]);
  const [currentProject, setCurrentProject] = useState(null);
  const [myProjects, setMyProjects] = useState([]);
  const [sharedProjects, setSharedProjects] = useState([]);
  const [currentMembers, setCurrentMembers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [dashboardTasks, setDashboardTasks] = useState([]);
  
  const { logout } = useAuth();

  // --- YARDIMCI FONKSİYONLAR ---
  // getAuthHeaders ARTIK YOK. api.js bunu otomatik yapıyor.

  const handleError = (err) => {
    const message = err.response?.data?.message || 'An error occurred.';
    console.error("API Error:", message);
    setError(message);
    if (err.response?.status === 401) {
        // Token geçersizse çıkış yap
        logout();
    }
    // throw new Error(message); // Uygulamayı kırmamak için throw'u kaldırdım, sadece error state güncellensin.
  };
  
  // --- PROJE LİSTESİ FONKSİYONLARI ---
  
  // (Dashboard için)
  const fetchProjects = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await api.get('/api/projects');
      setProjects(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  }, []);

  // (MyProjectsPage için)
  const fetchMyProjects = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await api.get('/api/projects/my-projects');
      setMyProjects(response.data); 
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  }, []);

  // (SharedProjectsPage için)
  const fetchSharedProjects = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await api.get('/api/projects/shared-projects');
      setSharedProjects(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  }, []);

  // Proje oluşturma
  const createProject = async (name, description) => {
    setLoading(true);
    try {
      const response = await api.post('/api/projects', { name, description });
      // Yeni proje geldi, state'leri güncelle
      setProjects(prev => [response.data, ...prev]);
      setMyProjects(prev => [response.data, ...prev]);
      return response.data;
    } catch (err) {
      handleError(err);
      throw err; 
    } finally {
      setLoading(false);
    }
  };

  // Proje detayını getir
  const fetchProjectById = useCallback(async (projectId) => {
    setLoading(true);
    setError(null);
    try {
      const response = await api.get(`/api/projects/${projectId}`);
      setCurrentProject(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  }, []);

  // Proje görünürlüğünü güncelle
  const updateProjectVisibility = async (projectId, isPublic) => {
    try {
      const response = await api.put(
        `/api/projects/${projectId}/visibility`, 
        { is_public: isPublic }
      );
      setCurrentProject(response.data);
      return response.data; 
    } catch (err) {
      handleError(err);
      throw err;
    }
  };

  const updateTask = async (projectId, taskId, data) => {
    try {
      const response = await api.put(`/api/projects/${projectId}/tasks/${taskId}`, data);
      return response.data;
    } catch (err) { handleError(err); throw err; }
  };

  // Görevi Sil
  const deleteTask = async (projectId, taskId) => {
    try {
      await api.delete(`/api/projects/${projectId}/tasks/${taskId}`);
      return true;
    } catch (err) { handleError(err); throw err; }
  };
  
  const fetchDashboardTasks = useCallback(async () => {
    setLoading(true);
    try {
      const response = await api.get('/api/projects/user/all-tasks');
      setDashboardTasks(response.data);
    } catch (err) {
      handleError(err);
    } finally {
      setLoading(false);
    }
  }, []);

  // --- ISSUE FONKSİYONLARI ---
  const fetchIssues = async (projectId) => {
    try {
      const response = await api.get(`/api/projects/${projectId}/issues`);
      return response.data;
    } catch (err) { handleError(err); throw err; }
  };
  
  const createIssue = async (projectId, text) => {
      try {
        const response = await api.post(`/api/projects/${projectId}/issues`, { text });
        return response.data;
      } catch (err) { handleError(err); throw err; }
  };
  
  const updateIssue = async (projectId, issueId, data) => {
    try {
      const response = await api.put(
        `/api/projects/${projectId}/issues/${issueId}`, 
        data
      );
      return response.data;
    } catch (err) { handleError(err); throw err; }
  };
  
  // --- YORUM FONKSİYONLARI ---
  const fetchComments = async (projectId) => {
    try {
      const response = await api.get(`/api/projects/${projectId}/comments`);
      return response.data;
    } catch (err) { handleError(err); throw err; }
  };

  const addComment = async (projectId, text) => {
    try {
      const response = await api.post(`/api/projects/${projectId}/comments`, { text });
      return response.data;
    } catch (err) { handleError(err); throw err; }
  };

  const fetchIssueComments = async (projectId, issueId) => {
    try {
      const response = await api.get(`/api/projects/${projectId}/issues/${issueId}/comments`);
      return response.data;
    } catch (err) { handleError(err); throw err; }
  };

  const addIssueComment = async (projectId, issueId, text) => {
    try {
      const response = await api.post(`/api/projects/${projectId}/issues/${issueId}/comments`, { text });
      return response.data;
    } catch (err) { handleError(err); throw err; }
  };

  // --- ÜYE FONKSİYONLARI ---
  const fetchMembers = async (projectId) => {
    try {
        const response = await api.get(`/api/projects/${projectId}/members`);
        setCurrentMembers(response.data); 
        return response.data; 
    } catch (err) { handleError(err); throw err; }
  };
  
  const addMember = async (projectId, username, role) => {
    try {
      const response = await api.post(
        `/api/projects/${projectId}/members`, 
        { username: username, role: role }
      );
      setCurrentMembers(prev => [...prev, response.data]);
      return response.data;
    } catch (err) { 
      handleError(err); 
      throw err; 
    }
  };

  const removeMember = async (projectId, userId) => {
    try {
      await api.delete(`/api/projects/${projectId}/members/${userId}`);
      setCurrentMembers(prev => prev.filter(member => member.id !== userId));
      return true; 
    } catch (err) {
      handleError(err);
      throw err;
    }
  };

  // === YORUM YÖNETİMİ FONKSİYONLARI ===

  const likeComment = async (projectId, commentId) => {
    try {
      const response = await api.post(`/api/projects/${projectId}/comments/${commentId}/like`, {});
      return response.data; 
    } catch (err) {
      handleError(err);
      throw err;
    }
  };

  const editComment = async (projectId, commentId, text) => {
    try {
      const response = await api.put(
        `/api/projects/${projectId}/comments/${commentId}`, 
        { text: text }
      );
      return response.data; 
    } catch (err) {
      handleError(err);
      throw err; 
    }
  };

  const deleteComment = async (projectId, commentId) => {
    try {
      await api.delete(`/api/projects/${projectId}/comments/${commentId}`);
      return true; 
    } catch (err) {
      handleError(err);
      throw err;
    }
  };

  const fetchFiles = async (projectId) => {
    try {
      const response = await api.get(`/api/projects/${projectId}/files`);
      return response.data;
    } catch (err) { handleError(err); }
  };

  const uploadFile = async (projectId, fileObj) => {
    try {
      const formData = new FormData();
      formData.append('file', fileObj); 

      // Not: api.js token'ı ekler, ama multipart için content-type'ı ezmemiz gerekebilir
      // veya axios formdata'yı görünce otomatik ayarlar. 
      // api.js'de varsayılan 'application/json' olduğu için burada override ediyoruz.
      const response = await api.post(
        `/api/projects/${projectId}/files`, 
        formData, 
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        }
      );
      return response.data;
    } catch (err) { 
      handleError(err); 
      throw err; 
    }
  };

  const deleteFile = async (projectId, fileId) => {
    try {
      await api.delete(`/api/projects/${projectId}/files/${fileId}`);
      return true;
    } catch (err) { handleError(err); }
  };

  const updateTasksVisibility = async (projectId, isPublic) => {
    try {
      const response = await api.put(`/api/projects/${projectId}/settings/tasks-visibility`, { is_tasks_public: isPublic });
      setCurrentProject(prev => ({ ...prev, is_tasks_public: response.data.is_tasks_public }));
    } catch (err) { handleError(err); }
  };

  const fetchTasks = async (projectId) => {
    try {
      const response = await api.get(`/api/projects/${projectId}/tasks`);
      return response.data;
    } catch (err) { 
      if (err.response && err.response.status === 403) return null;
      handleError(err); 
    }
  };

  const createTask = async (projectId, taskData) => {
    try {
      const response = await api.post(`/api/projects/${projectId}/tasks`, taskData);
      return response.data;
    } catch (err) { handleError(err); }
  };

  const updateTaskStatus = async (projectId, taskId, newStatus) => {
    try {
      const response = await api.put(`/api/projects/${projectId}/tasks/${taskId}`, { status: newStatus });
      return response.data;
    } catch (err) { handleError(err); }
  };
  
  const updateProjectDetails = async (projectId, name, description, longDescription) => {
    setLoading(true);
    try {
      const response = await api.put(
        `/api/projects/${projectId}`, 
        { name, description, long_description: longDescription }
      );
      setCurrentProject(response.data);
      return response.data;
    } catch (err) {
      handleError(err);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const value = {
