// src/context/ProjectContext.js

import React, { createContext, useState, useContext } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const API_URL = 'http://localhost:5000/api/projects';

export const ProjectContext = createContext();
export const useProjectContext = () => useContext(ProjectContext);

export const ProjectProvider = ({ children }) => {
  const [projects, setProjects] = useState([]);
  const [currentProject, setCurrentProject] = useState(null);
  const [myProjects, setMyProjects] = useState([]);
  const [sharedProjects, setSharedProjects] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const { token, logout } = useAuth();

  // --- YARDIMCI FONKSİYONLAR ---
  const getAuthHeaders = () => {
    if (!token) throw new Error('Token not found. Please log in.');
    return { headers: { 'Authorization': `Bearer ${token}` } };
  };
  const handleError = (err) => {
    const message = err.response?.data?.message || 'An error occurred.';
    setError(message);
    if (err.response?.status === 401) logout();
    throw new Error(message);
  };
  
  // --- PROJE LİSTESİ FONKSİYONLARI ---
  const fetchProjects = async () => { /* ... */ };
  const fetchMyProjects = async () => { /* ... */ };
  const fetchSharedProjects = async () => { /* ... */ };
  const createProject = async (name, description) => { /* ... */ };
  const fetchProjectById = async (projectId) => { /* ... */ };
  const updateProjectVisibility = async (projectId, isPublic) => { /* ... */ };
  
  // --- ISSUE FONKSİYONLARI ---
  const fetchIssues = async (projectId) => { /* ... */ };
  const createIssue = async (projectId, text) => { /* ... */ };
  const updateIssue = async (projectId, issueId, data) => { /* ... */ };
  
  // --- YORUM FONKSİYONLARI ---
  const fetchComments = async (projectId) => { /* ... */ };
  const addComment = async (projectId, text) => { /* ... */ };
  const fetchIssueComments = async (projectId, issueId) => { /* ... */ };
  const addIssueComment = async (projectId, issueId, text) => { /* ... */ };

  // --- ÜYE FONKSİYONLARI ---
  const fetchMembers = async (projectId) => { /* ... */ };
  const addMember = async (projectId, username) => { /* ... */ };

  // === YORUM YÖNETİMİ FONKSİYONLARI ===

  // Yorumu Beğen (Like/Unlike)
  const likeComment = async (projectId, commentId) => {
    try {
      const response = await axios.post(
        `${API_URL}/${projectId}/comments/${commentId}/like`, 
        {}, // Boş body
        getAuthHeaders()
      );
      return response.data; // Güncel 'likes_user_ids' dizisini döndür
    } catch (err) {
      handleError(err);
      throw err; // Hatayı component'e fırlat
    }
  };

  // Yorumu Düzenle (Edit)
  const editComment = async (projectId, commentId, text) => {
    try {
      const response = await axios.put(
        `${API_URL}/${projectId}/comments/${commentId}`, 
        { text: text }, // Yeni metni gönder
        getAuthHeaders()
      );
      return response.data; // Güncellenmiş yorum objesini döndür
    } catch (err) {
      handleError(err);
      throw err; // Hatayı 'Comment.js' component'ine fırlat
    }
  };

  // Yorumu Sil (Delete)
  const deleteComment = async (projectId, commentId) => {
    try {
      await axios.delete(
        `${API_URL}/${projectId}/comments/${commentId}`, 
        getAuthHeaders()
      );
      return true; // Başarılı
    } catch (err) {
      handleError(err);
      throw err; // Hatayı 'Comment.js' component'ine fırlat
    }
  };
  
  // Context'in sağlayacağı tüm değerler
  const value = {
    projects, currentProject, myProjects, sharedProjects, loading, error,
    fetchProjects, fetchMyProjects, fetchSharedProjects,
    fetchProjectById, createProject, updateProjectVisibility,
    fetchIssues, createIssue, updateIssue,
    fetchComments, addComment, fetchIssueComments, addIssueComment,
    fetchMembers, addMember,
    
    // === YENİ EKLENEN FONKSİYONLAR ===
    likeComment, editComment, deleteComment
  };

  return (
    <ProjectContext.Provider value={value}>{children}</ProjectContext.Provider>
  );
};
