// src/components/projects/ProjectFiles.js

import React, { useState, useEffect } from 'react';
import { useProjectContext } from '../../context/ProjectContext';
import { 
  FiFile, FiDownload, FiTrash2, FiUpload, FiLoader, 
  FiFileText, FiX, FiEye, FiCheckSquare, FiSquare, FiChevronLeft, FiChevronRight, FiExternalLink
} from 'react-icons/fi';

export default function ProjectFiles() {
  const { currentProject, fetchFiles, uploadFile, deleteFile } = useProjectContext();
  
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [selectedIds, setSelectedIds] = useState([]);
  const [previewFile, setPreviewFile] = useState(null);

  const userRole = currentProject?.currentUserRole;
  const canEdit = userRole === 'owner' || userRole === 'editor';

  // Localhost kontrolü (Office viewer uyarısı için)
  const isLocalhost = window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1';

  useEffect(() => {
    if (currentProject) {
      setLoading(true);
      fetchFiles(currentProject.id)
        .then(data => setFiles(Array.isArray(data) ? data : []))
        .finally(() => setLoading(false));
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentProject]);

  // Klavye olayları (Preview için)
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!previewFile) return;
      if (e.key === 'ArrowRight') handleNextPrev('next');
      if (e.key === 'ArrowLeft') handleNextPrev('prev');
      if (e.key === 'Escape') setPreviewFile(null);
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  });

  const handleFileUpload = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    if (selectedFiles.length === 0) return;

    setUploading(true);
    try {
      const uploadPromises = selectedFiles.map(file => uploadFile(currentProject.id, file));
      const newFiles = await Promise.all(uploadPromises);
      // Listeyi güncelle
      setFiles(prevFiles => [...newFiles, ...prevFiles]); 
    } catch (error) {
      console.error("Upload failed", error);
      alert("Hata: Dosyalar yüklenemedi.");
    } finally {
      setUploading(false);
      e.target.value = null; 
    }
  };

  const handleDeleteSelected = async () => {
    if (window.confirm(`Seçili ${selectedIds.length} dosyayı silmek istediğine emin misin?`)) {
      setLoading(true);
      try {
        await Promise.all(selectedIds.map(id => deleteFile(currentProject.id, id)));
        setFiles(files.filter(f => !selectedIds.includes(f.id)));
        setSelectedIds([]);
      } catch (err) {
        alert("Bazı dosyalar silinemedi.");
      } finally {
        setLoading(false);
      }
    }
  };

  const toggleSelect = (id, e) => {
    e.stopPropagation();
    if (selectedIds.includes(id)) {
      setSelectedIds(selectedIds.filter(itemId => itemId !== id));
    } else {
      setSelectedIds([...selectedIds, id]);
    }
  };

  const toggleSelectAll = () => {
    if (selectedIds.length === files.length) {
      setSelectedIds([]);
    } else {
      setSelectedIds(files.map(f => f.id));
    }
  };

  const handleNextPrev = (direction) => {
    if (!previewFile) return;
    const currentIndex = files.findIndex(f => f.id === previewFile.id);
    let newIndex;
    if (direction === 'next') {
      newIndex = currentIndex + 1 < files.length ? currentIndex + 1 : 0; 
    } else {
      newIndex = currentIndex - 1 >= 0 ? currentIndex - 1 : files.length - 1; 
    }
    setPreviewFile(files[newIndex]);
  };

  const getFileUrl = (filePath) => {
    if (!filePath) return '';
    const cleanPath = filePath.replace(/\\/g, '/');
    return `http://localhost:5000/${cleanPath}`;
  };

  // Office dosyaları için yeni tipler eklendi
  const getFileType = (filename) => {
    if (!filename) return 'other';
    const ext = filename.split('.').pop().toLowerCase();
    
    if (['jpg', 'jpeg', 'png', 'gif', 'webp', 'svg'].includes(ext)) return 'image';
    if (ext === 'pdf') return 'pdf';
    if (['doc', 'docx'].includes(ext)) return 'word';
    if (['ppt', 'pptx'].includes(ext)) return 'powerpoint';
    if (['txt', 'md', 'json', 'xml', 'js', 'css', 'html', 'csv', 'log'].includes(ext)) return 'text';
    
    return 'other';
  };

  // Önizleme URL'ini oluşturur (Office Viewer dahil)
  const getPreviewContent = () => {
    const type = getFileType(previewFile.filename);
    const url = getFileUrl(previewFile.file_path);

    // 1. Resimler
    if (type === 'image') {
      return (
        <img 
          src={url} 
          alt="Preview" 
          className="max-w-full max-h-full object-contain drop-shadow-2xl"
        />
      );
    }

    // 2. Word ve PowerPoint (Office Viewer Kullanılır)
    if (type === 'word' || type === 'powerpoint') {
      if (isLocalhost) {
        return (
          <div className="text-center p-8 bg-surface border border-border rounded-xl max-w-md">
            <h3 className="text-xl font-bold text-text-main mb-2">Localhost Uyarısı</h3>
            <p className="text-text-secondary mb-4">
              Microsoft Office Viewer, güvenlik nedeniyle localhost üzerindeki dosyalara erişemez. 
              Projeyi canlı sunucuya (deploy) aldığınızda bu dosyalar otomatik olarak burada açılacaktır.
            </p>
            <a href={url} download className="inline-flex items-center px-4 py-2 bg-primary text-white rounded-lg hover:bg-primary-hover">
              <FiDownload className="mr-2" /> Dosyayı İndirip Aç
            </a>
          </div>
        );
      }
      // Canlı sunucuda ise Microsoft Viewer iframe'i döner
      const viewerUrl = `https://view.officeapps.live.com/op/embed.aspx?src=${encodeURIComponent(url)}`;
      return (
        <iframe 
          src={viewerUrl} 
          title="Office Preview" 
          className="w-full h-full border-none bg-white"
        />
      );
    }

    // 3. PDF ve Text Dosyaları (Tarayıcı native açar)
    if (type === 'pdf' || type === 'text') {
      return (
        <iframe 
          src={url} 
          title="File Preview" 
          className="w-full h-full border-none bg-white"
        />
      );
    }

    // 4. Desteklenmeyen Türler
    return (
      <div className="text-center text-white bg-gray-800 p-10 rounded-2xl border border-gray-700">
        <FiFileText size={60} className="mx-auto mb-4 text-gray-400"/>
        <p className="text-gray-300 mb-6">Bu dosya formatı önizlenemiyor.</p>
        <a href={url} download className="px-6 py-2 bg-primary hover:bg-primary-hover rounded-lg font-bold text-white transition-colors">
          İndir
        </a>
      </div>
    );
  };

  return (
    <div className="space-y-6 relative">
      {/* HEADER */}
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-6">
        <div className="flex items-center gap-4">
          <h2 className="text-2xl font-semibold text-text-main">Project Files</h2>
          {selectedIds.length > 0 && canEdit && (
            <button 
              onClick={handleDeleteSelected}
              className="flex items-center px-3 py-1.5 bg-red-50 dark:bg-red-900/20 text-red-600 dark:text-red-400 hover:bg-red-100 dark:hover:bg-red-900/40 rounded-lg transition-colors text-sm border border-red-200 dark:border-red-800"
            >
              <FiTrash2 className="mr-2" /> Delete Selected ({selectedIds.length})
            </button>
          )}
        </div>
        
        {canEdit && (
          <div className="relative">
            <input 
              type="file" multiple onChange={handleFileUpload}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
              disabled={uploading}
            />
            <button className={`relative z-0 flex items-center px-4 py-2 rounded-lg transition-colors ${uploading ? 'bg-text-secondary cursor-not-allowed' : 'bg-primary hover:bg-primary-hover'} text-white shadow-lg`}>
              {uploading ? <FiLoader className="animate-spin mr-2" /> : <FiUpload className="mr-2" />}
              {uploading ? 'Uploading...' : 'Upload Files'}
            </button>
          </div>
        )}
      </div>

      {/* LİSTE */}
      <div className="bg-surface rounded-xl overflow-hidden shadow-sm border border-border">
        {/* Header Row */}
        <div className="flex items-center bg-app p-3 border-b border-border text-xs font-bold text-text-secondary uppercase">
          <div className="w-10 text-center">
            {canEdit && (
              <button onClick={toggleSelectAll} className="hover:text-text-main">
                {files.length > 0 && selectedIds.length === files.length ? <FiCheckSquare size={16}/> : <FiSquare size={16}/>}
              </button>
            )}
          </div>
          <div className="flex-1 px-4">Filename</div>
          <div className="w-32 hidden sm:block">Uploaded By</div>
          <div className="w-32 text-right hidden sm:block">Date</div>
          <div className="w-16 text-center">View</div>
        </div>

        {loading ? (
          <div className="flex justify-center p-10"><FiLoader className="animate-spin text-primary" size={30} /></div>
        ) : files.length === 0 ? (
          <div className="p-10 text-center text-text-secondary">No files uploaded yet.</div>
        ) : (
          <ul className="divide-y divide-border">
            {files.map(file => {
              const isSelected = selectedIds.includes(file.id);
              const type = getFileType(file.filename);

              return (
                <li 
                  key={file.id} 
                  onClick={() => setPreviewFile(file)}
                  className={`flex items-center p-3 hover:bg-surface-hover transition-colors cursor-pointer group ${isSelected ? 'bg-primary/5' : ''}`}
                >
                  <div className="w-10 flex justify-center" onClick={(e) => e.stopPropagation()}>
                    {canEdit && (
                      <button onClick={(e) => toggleSelect(file.id, e)} className={`transition-colors ${isSelected ? 'text-primary' : 'text-text-secondary hover:text-text-main'}`}>
                        {isSelected ? <FiCheckSquare size={18}/> : <FiSquare size={18}/>}
                      </button>
                    )}
                  </div>

                  <div className="flex-1 px-4 flex items-center min-w-0">
                    <div className={`p-2 rounded-lg mr-3 ${
                        type === 'image' ? 'bg-purple-100 text-purple-600 dark:bg-purple-900/30 dark:text-purple-400' : 
                        (type === 'word' || type === 'powerpoint') ? 'bg-orange-100 text-orange-600 dark:bg-orange-900/30 dark:text-orange-400' :
                        (type === 'pdf' || type === 'text') ? 'bg-blue-100 text-blue-600 dark:bg-blue-900/30 dark:text-blue-400' : 
                        'bg-app text-text-secondary'
                    }`}>
                      {type === 'image' ? <FiFileText /> : <FiFile />}
                    </div>
                    <span className="text-text-main font-medium truncate">{file.filename}</span>
                  </div>

                  <div className="w-32 text-sm text-text-secondary hidden sm:block truncate">{file.uploader_name}</div>
                  <div className="w-32 text-right text-sm text-text-secondary hidden sm:block font-mono">{new Date(file.created_at).toLocaleDateString()}</div>

                  <div className="w-16 flex justify-center" onClick={(e) => e.stopPropagation()}>
                    <a 
                      href={getFileUrl(file.file_path)} 
                      download
                      className="p-1.5 text-text-secondary hover:text-text-main hover:bg-surface-hover rounded transition-colors"
                      title="Download"
                    >
                      <FiDownload size={16} />
                    </a>
                  </div>
                </li>
              );
            })}
          </ul>
        )}
      </div>

      {/* --- PREVIEW MODAL --- */}
      {previewFile && (
        // z-index 99999 olarak güncellendi (Header'ın üstünde kalması için)
        // p-12 kaldırıldı, h-screen ve w-screen ile tam ekran yapıldı
        <div className="fixed inset-0 z-[99999] bg-black/95 backdrop-blur-sm flex flex-col animate-fade-in">
          
          {/* Modal Header */}
          <div className="flex items-center justify-between px-6 py-4 bg-black/50 border-b border-white/10 text-white z-50">
             <div className="flex items-center gap-4">
               <button onClick={() => setPreviewFile(null)} className="p-2 hover:bg-white/10 rounded-full transition-colors">
                 <FiX size={24} />
               </button>
               <span className="font-bold truncate max-w-md">{previewFile.filename}</span>
             </div>
             
             <div className="flex items-center gap-3">
               <a href={getFileUrl(previewFile.file_path)} target="_blank" rel="noreferrer" className="flex items-center gap-2 px-4 py-2 hover:bg-white/10 rounded-lg text-sm font-medium transition-colors">
                 <FiExternalLink /> Yeni Sekmede Aç
               </a>
               <a href={getFileUrl(previewFile.file_path)} download className="flex items-center gap-2 px-4 py-2 bg-primary hover:bg-primary-hover rounded-lg text-sm font-bold transition-colors">
                 <FiDownload /> İndir
               </a>
             </div>
          </div>

          {/* Modal Content - Tam Ekran ve Ortalı */}
          <div className="flex-1 relative w-full h-full flex items-center justify-center overflow-hidden bg-black/20">
            
            {/* Navigasyon Butonları */}
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('prev'); }} className="absolute left-4 z-50 text-white/50 hover:text-white p-4 hover:bg-white/10 rounded-full transition-all">
                <FiChevronLeft size={48} />
            </button>
            <button onClick={(e) => { e.stopPropagation(); handleNextPrev('next'); }} className="absolute right-4 z-50 text-white/50 hover:text-white p-4 hover:bg-white/10 rounded-full transition-all">
                <FiChevronRight size={48} />
            </button>

            {/* İçerik Render */}
            <div className="w-full h-full flex items-center justify-center p-4">
               {getPreviewContent()}
            </div>

            {/* Alt Bilgi */}
            <div className="absolute bottom-6 z-50 pointer-events-none">
                <span className="bg-black/60 text-white/80 px-4 py-1.5 rounded-full text-xs font-medium backdrop-blur-md border border-white/10">
                    {files.findIndex(f => f.id === previewFile.id) + 1} / {files.length}
                </span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
