// src/pages/LoginPage.js

import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
// İkonlar
import { FiMail, FiLock, FiArrowLeft, FiLoader, FiAlertCircle } from 'react-icons/fi'; 
// Arka plan resmi
import BackgroundImage from '../assets/background.jpg'; 

function LoginPage() {
  // Backend ile uyum için state'i email olarak tutuyoruz (eğer backend username istiyorsa aşağıda açıklama var)
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const navigate = useNavigate();
  const { login } = useAuth();

  const handleSubmit = async (event) => { 
    event.preventDefault(); 
    setError('');
    
    if (!email || !password) {
        setError("Please fill in all fields.");
        return;
    }

    setIsSubmitting(true);
    
    try {
      // AuthContext'teki login fonksiyonunu çağırıyoruz
      // DİKKAT: Backend'iniz 'username' bekliyorsa, buradaki değişken adını değiştirmeden
      // sadece backend'e giden veriyi { username: email, ... } şeklinde de düşünebilirsiniz 
      // veya doğrudan email kullanıyorsanız sorun yok.
      const result = await login(email, password);

      if (result.success) {
        navigate('/'); // Başarılıysa Dashboard'a git
      } else {
        setError(result.message || 'Login failed. Check your credentials.');
      }
    } catch (err) {
      setError('An error occurred. Please try again later.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div 
      className="relative flex items-center justify-center min-h-screen bg-cover bg-center"
      style={{ 
        backgroundImage: BackgroundImage ? `url(${BackgroundImage})` : 'none',
        backgroundColor: '#111827' 
      }}
    >
      {/* Koyu Overlay */}
      <div className="absolute inset-0 bg-black/60 backdrop-blur-[2px]"></div>

      {/* 1. SOL ÜST KÖŞE - GERİ DÖN BUTONU */}
      <Link 
        to="/" 
        className="absolute top-8 left-8 z-20 flex items-center text-gray-400 hover:text-white transition-colors group"
      >
        <FiArrowLeft className="mr-2 group-hover:-translate-x-1 transition-transform" size={24} />
        <span className="font-semibold text-lg">Back to Home</span>
      </Link>

      {/* FORM KARTI */}
      <div className="relative z-10 w-full max-w-md p-8 bg-gray-900/80 border border-gray-700 rounded-2xl shadow-2xl backdrop-blur-md animate-fade-in">
        
        <div className="text-center mb-8">
          <h2 className="text-3xl font-bold text-white tracking-tight">Welcome Back</h2>
          <p className="mt-2 text-sm text-gray-400">Sign in to access your workspace</p>
        </div>

        <form className="space-y-6" onSubmit={handleSubmit}>
            
            {/* HATA MESAJI ALANI */}
            {error && (
              <div className="bg-red-500/10 border border-red-500/50 text-red-200 px-4 py-3 rounded-lg flex items-center text-sm">
                <FiAlertCircle className="mr-2 flex-shrink-0" />
                {error}
              </div>
            )}

            {/* EMAIL / USERNAME INPUT */}
             <div className="relative group">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <FiMail className="w-5 h-5 text-gray-500 group-focus-within:text-blue-500 transition-colors" />
                </div>
                <input
                  id="email"
                  type="text" 
                  // Eğer email zorunluluğu yoksa type="text" kalabilir
                  required
                  className="w-full py-3 pl-10 pr-4 text-gray-200 bg-gray-800/50 border border-gray-600 rounded-xl placeholder:text-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                  placeholder="Email or Username"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              
              {/* PASSWORD INPUT */}
              <div className="relative group">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <FiLock className="w-5 h-5 text-gray-500 group-focus-within:text-blue-500 transition-colors" />
                </div>
                <input
                  id="password"
                  type="password"
                  required
                  className="w-full py-3 pl-10 pr-4 text-gray-200 bg-gray-800/50 border border-gray-600 rounded-xl placeholder:text-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>

              <div>
                {/* SIGN IN BUTONU */}
                <button
                  type="submit"
                  disabled={isSubmitting}
                  className="w-full px-4 py-3 font-bold text-white bg-blue-600 rounded-xl shadow-lg hover:bg-blue-700 hover:shadow-blue-600/30 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-900 focus:ring-blue-500 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed transform active:scale-[0.98] flex justify-center items-center"
                >
                  {isSubmitting ? (
                    <>
                      <FiLoader className="animate-spin mr-2" /> Signing In...
                    </>
                  ) : 'Sign In'}
                </button>
                
                {/* 2. FORGOT PASSWORD (Butonun altında) */}
                <div className="flex justify-center mt-6">
                  <Link to="/forgot-password" className="text-sm font-medium text-gray-400 hover:text-blue-400 transition-colors">
                    Forgot Password?
                  </Link>
                </div>
              </div>
        </form>
        
        {/* FOOTER - SIGN UP */}
        <div className="text-center mt-6 pt-6 border-t border-gray-700">
          <p className="text-sm text-gray-400">
            Don't have an account?{' '}
            <Link to="/register" className="font-semibold text-blue-500 hover:text-blue-400 transition-colors ml-1">
              Sign Up
            </Link>
          </p>
        </div>

      </div>
    </div>
  );
}

export default LoginPage;
